css:
@-moz-document domain(www.youtube.com) {

	@media (min-width: 3800px) {
	  ytd-masthead#masthead  div#background {
		background: rgba(var(--yt-spec-base-background), 0.5);
	  }

	  ytd-page-manager#page-manager.ytd-app {
		margin-top: 0;
		--ytd-toolbar-offset: 0;
	  }

	  div#center.ytd-masthead {
		opacity: 0;
		transition-duration: 0.2s;
	  }

	  div#center.ytd-masthead:hover, div#center.ytd-masthead:focus-within {
		opacity: 1;
		z-index: 2222;
	  }

	  button#search-icon-legacy {
		background-color: var(--yt-spec-base-background) !important;
	  }
  
	  yt-icon#guide-icon[icon="yt-icons:menu"] > svg {
	    box-shadow: black 0px 0px 10px -6px;
	    background-color: #0001;
	  }  
  
	  yt-icon#logo-icon.ytd-logo > svg {
	    box-shadow: black 0px 0px 10px -6px;
	    background-color: #0001;
	  }
  
	  ytd-topbar-menu-button-renderer yt-icon > svg {
	    box-shadow: black 0px 0px 10px -6px;
	    background-color: #0001;
	  }
  
	  ytd-notification-topbar-button-renderer yt-icon > svg {
	    box-shadow: black 0px 0px 10px -6px;
	    background-color: #0001;
	  }

	  ytd-browse[page-subtype=home] div#header.ytd-rich-grid-renderer {
		max-width: 1336px;
		margin-right: auto;
		z-index: 2220;
	  }

	  ytd-browse[page-subtype=home] div#chips-wrapper.ytd-feed-filter-chip-bar-renderer {
		top: 0;
		background-color: rgba(var(--yt-spec-base-background), 0.5) !important;
		max-width: 1336px;
	  }
	  
	  ytd-browse[page-subtype=channels] {
		--ytd-toolbar-offset: var(--ytd-masthead-height, var(--ytd-toolbar-height));
		margin-top: var(--ytd-masthead-height,var(--ytd-toolbar-height));
	  }

	  ytd-watch-flexy:not([fullscreen]) div#player-theater-container {
		max-height: calc(100vh - 112px) !important;
	  }

	  ytd-watch-flexy:not([fullscreen]) div.ytp-chrome-top {
	    margin-top: var(--ytd-masthead-height, var(--ytd-toolbar-height));
	  }
	  
	}
}
