@-moz-document domain("9gag.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.drawer-menu-item[data-v-56c4f993]
{
	font-size: 1.52rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#sidebar,
#primis_container_div,

.billboard,
.campaign-banner img,
.inline-ad-container,
.ui-post-creator .ui-post-creator__author.promoted,

[id*='dfp']

{
	display: none!important;
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.drawer .campaign-banner {
    height: 0;
}

/* Widths 
_____________________________________________________________________________________________________*/

#main
{
	width: auto!important;
}

section#list-view-2 .post-container .post-view img,
section#list-view-2 .post-container
{
    width: 100%!important;
}

#container.drawer
{
	width: 98%!important;
}

#page {
    width: 89%!important;
}

.drawer {
    width: 15%!important;
}

}

@-moz-document domain("9gag.com") {
/* Media Query */

@media (min-width: 1921px) and (max-width: 2560px) {
	

	
}

@media (min-width: 1080px) and (max-width: 1920px) {
		

	
}
}

@-moz-document domain("9gag.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.ui-post-creator__creation,

time
 {
	color: #07beff!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("9gag.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

selector
{
	background-image: none!important;
}

/* Background 
_____________________________________________________________________________________________________*/

selector
{
	background: #060505!important;
	background-color: #060505!important;
}

selector
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

selector
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.ui-btn.btn-color-primary
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.ui-btn.btn-color-primary::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.ui-btn.btn-color-primary:hover::before
 {
  opacity: 1!important;
}

}

@-moz-document domain("9gag.com") {
/* Image Settings */

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("9gag.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}

body.theme-dark .ui-base-icon[data-v-9ea500d2],
body.theme-dark .ui-base-icon {
    filter: none!important;
}

/* Clock ___________________________________________________________________________*/

[href="/fresh"] svg {
	background: url(https://i.ibb.co/nfDfPPb/clock.png) no-repeat!important;
	box-sizing: border-box;
}

[href="/fresh"] svg
{
    background-size: 25px!important;
    height: 36px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

/* Edit _______________________________________________________________*/

.upload .ui-btn .ui-base-icon {
    margin: 0 18px;
}

/* Edit ____________________________*/

.upload svg {
	background: url(https://i.ibb.co/GtTykGg/pencil.png) no-repeat!important;
	box-sizing: border-box;
}

/* No Edit ____________________________*/

Selector {
	background: url(https://i.ibb.co/0FWtkSY/pencil-no-edit.png) no-repeat!important;
	box-sizing: border-box;
}

.upload svg {
    background-size: 25px!important;
    height: 26px!important;
    padding-left: 26px!important;
    width: 10px!important;
}

/* Home _____________________________________________________________________________*/

[href="/home"] svg
 {
    background: url(https://i.ibb.co/c63D58g/Home4.png) no-repeat!important;
    box-sizing: border-box;
}

[href="/home"] svg
 {
    background-size: 25px!important;
    height: 36px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

/* Notification ________________________________________________________________________*/

.notification .bell {
	background: url(https://i.ibb.co/8Px680k/Notification.png) no-repeat!important;
	box-sizing: border-box;
}

.notification .bell {
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}

/* Search ___________________________________________________________________________*/

.general-function .search
{
	background: url(https://i.ibb.co/cJFxJM6/search3.png) no-repeat!important;
	box-sizing: border-box;
}

.general-function .search
{
	background-size: 35px!important;
	height: 36px!important;
	padding-left: 36px!important;
	width: 1px!important;
}

/* Star ___________________________________________________________________________*/

/* On ___________________*/

.button.favorite svg
{
	background: url(https://i.ibb.co/WH0qS2k/star.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off __________________*/

.drawer-menu-item__actions svg
{
	background: url(https://i.ibb.co/VBcDjnC/star-empty-glow.png) no-repeat!important;
	box-sizing: border-box;
}

.button.favorite svg,
.drawer-menu-item__actions svg
{
	background-size: 25px!important;
	height: 36px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

/* Top _____________________________________________________________________________*/

[href="/top"] svg
 {
    background: url(https://i.ibb.co/5hvFnXW/bar-chart.png) no-repeat!important;
    box-sizing: border-box;
}

[href="/top"] svg
 {
    background-size: 25px!important;
    height: 36px!important;
    padding-left: 26px!important;
    width: 1px!important;
}

/* Trending _____________________________________________________________________________*/

[href="/trending"] svg
 {
    background: url(https://i.ibb.co/Tc9rFcz/trending.png) no-repeat!important;
    box-sizing: border-box;
}

[href="/trending"] svg
 {
    background-size: 25px!important;
    height: 36px!important;
    padding-left: 26px!important;
    width: 1px!important;
}


}

@-moz-document domain("9gag.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
