css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("games.rofl.to") {

#bottom-footer,#footer,#sidebar-right,#advert-right,#top-information,.logo

{position:fixed !important;
    width:0px !important;
top:-1000px!important;
    height:0px !important;
visibility:hidden !important;

}



html,body,#wrap,#body,.with-sidebar-right#body-right,#content,.box,.box-inner,.container,.container-inner,#flash-game
{
float: left!important;
width: 100%!important;
}

#tab-navigation
{
position:absolute!important;
left:300px!important;
top:20px!important;
}
#top-bar
{
position:absolute!important;
left:00px!important;
top:50px!important;
z-index:8!important;
}
#body
{
position:absolute!important;
left:00px!important;
top:-50px!important;
}
#header
{z-index:5!important;
background:white!important;
height:50px!important;}

#column-right
{
float:right!important;
position:absolute!important;
right:-200px!important;
top:200px!important;
}
#column-left
{
position:relative!important;
float: left!important;
width: 100%!important;
top:-140px!important;
}

}
