css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("xda-developers.com") {

body {background: #61a2f1 !important; color: #000000;margin: 0 auto !important; width: 1200px !important;}
a:hover, a:active, body_ahover {color: #6690c5 !important; text-decoration: none !important;}
textarea, .bginput {background: #ffffff !important;}
select {background: #ffffff !important;}
.page {background: #dae7f8 !important; color: #000000 !important;}
.page a:hover, .page a:active, .page_ahover {color: #6690c5 !important;}
.tborder {background: #d9e4f7 !important; color: #000000 !important;border: 1px solid #90b1d9 !important;}
.tcat a { color: #1c459c !important; }
.tcat a:hover, .tcat a:active, .tcat_ahover {color: #1c459c !important; text-decoration: none !important;}
.thead a:link, .thead_alink {color: #1c459c !important;}
.thead a:visited, .thead_avisited {color: #1c459c !important;}
.thead a:hover, .thead a:active, .thead_ahover {color: #1c459c !important;}
.tfoot a:link, .tfoot_alink {color: #1c459c !important;}
.tfoot a:visited, .tfoot_avisited {color: #1c459c !important;}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover {color: #1c459c !important; text-decoration: underline;}
.alt1, .alt1Active {background: #ffffff !important; color: #000000 !important;}
.alt2, .alt2Active {background: #e9f4ff !important; color: #000000 !important;}
.inlinemod {background: #ffffff !important; color: #000000 !important;}
.wysiwyg {background: #ffffff !important; color: #000000 !important; margin: 5px 10px 10px 10px !important; padding: 0px !important;}
.button {background: #ffffff !important; color: #000000 !important;}
.fjsel {background: #D0D0D0 !important; color: #000000 !important;}
.fjdpth0 {background: #ECECEC !important; color: #000000 !important;}
.panel {background: #ffffff !important; color: #000000 !important; padding: 9px !important; border: 2px solid #d9e4f7 !important;}
.panelsurround {background: #ffffff !important; color: #000000 !important;}
.vbmenu_control a {color: #1c459c !important;}
.vbmenu_option {background: #ECECEC !important; color: #000000 !important;}
.vbmenu_hilite {background: #D0D0D0 !important; color: #330000 !important;}
.alt3 {background-color:#d9e4f7 !important; font-family: Calibri !important; color: #303030 !important; font-style: italic !important;}

.tcat, .tfoot {
background: #d9e4f7 url(data:images/png;base64,) repeat-x top left !important;
color: #1c459c !important;}

.thead{
background: #d9e4f7 url(data:images/png;base64,) repeat-x top left !important;
color: #1c459c !important;}

.vbmenu_control
{
	background: #d9e4f7 url(data:images/png;base64,) repeat-x top left !important;
	color: #1c459c !important;
	padding: 3px 6px 3px 6px;
	white-space: nowrap;
}

img[src="http://forum.xda-developers.com/images/xdabuttons/collapse_thead.gif"],
img[src="/images/xdabuttons/collapse_thead.gif"],
img[src="http://forum.xda-developers.com/images/xdabuttons/collapse_tcat.gif"],
