css:
@-moz-document url-prefix(https://fabricmc.net/wiki/) {
  body {
    background-color: #121212 !important;
    color: #999 !important;
  }

  select, button {
    background: #313131;
    border: 1px solid #212121;
    color: #9f9f9f;
  }

  input {
    background: #c5c5c5;
    color: #060606;
  }



  pre.code{
    background: #090909;
    border: 1px solid #27272737;
    box-shadow: inset 0 0 1.3em #181818, inset 0 0 0.7em #373737ab, inset 0 0 1em #41414102;
  }

  pre{
    color: #999999;
  }

  code {
    background: #0a0a0a;
    color: #bbbbbb;
    text-shadow: 0 0 0 1px #272727;
    border: 1px transparent #78787821;
    box-shadow: inset 0 0 0.2em #181818, 0 0 0 0.1em #080808, 0 0 0 0.25em #09090952;
  }

  .code .kw1, .code .kw8 {
    color: /*[[fkw-col]]*/;
  }

  .code .kw13, .code .kw14, .code .kw15, .code .kw16, .code .me1, .code .me2 {
    color: /*[[efv-col]]*/;
  }

  .code .kw4, .code .re2 {
    color: /*[[kw-col]]*/;
  }

  .code .re1, .code .st0, .code .st_h {
    color: /*[[str-col]]*/;
  }

  .code .kw3, .code .kw7, .code .sy2 {
    color: /*[[dc-col]]*/;
  }

  .code .kw2 {
    color: /*[[bn-col]]*/;
  }

  .code .ln-xtra {
    background-color: /*[[extrln-col]]*//*[[extrln-alpha]]*/;
  }

  #dw__toc{
    background: #212121;
    border: 1px solid #414141;
  }

  .dokuwiki .page li .li, .dokuwiki .aside li .li {
    color: #999;
  }

  .dokuwiki div.page {
    background: #141414;
    border: 1px solid #222222;
    box-shadow: inset 0 0 0.5em #212121, inset 0 0 2em #373737, inset 0 0 1.5em #010101, 0 0 0.6em 0.1em #00000057, 0 0 0.5em #f1f1f110;
  }

  .dokuwiki dl.code dt, .dokuwiki dl.file dt {
    background: #040404;
    border: 1px solid #070707;
    box-shadow: 0 0 0.2em #090909, inset 0 0 0.1em #121212;
  }

  a{
    color: #1e67C8;
  }

  #dokuwiki__header h1 a,
  .site-nav .page-link,
  .site-title,
  .site-title:visited {
    color: #848484;
  }

  #dokuwiki__pagetools:hover div.tools ul {
    background-color: #010101;
    border-color: #888;
    box-shadow: 2px 2px 2px #777;
  }

  #dokuwiki__pagetools div.tools ul li a:active, #dokuwiki__pagetools div.tools ul li a:focus, #dokuwiki__pagetools div.tools ul li a:hover {
    background-color: #777;
  }

  a:link, a:visited {
    color: #1e67C8;
  }

  a.urlextern {
    color: #1c7b1f
  }

  .dokuwiki .pageId span {
    background-color: #010101;
    border-color: #333;
    box-shadow: 0 0 0.5em #111, 0 0 0.4em #010101, inset 0 0 0.4em #76767654, inset 0 0 0.7em #01010127;
    padding-top: 2px;
    padding-right: 1px;
    padding-left: 1px;
    border-top-left-radius: 0.2em;
    border-top-right-radius: 0.2em;
    margin-right: 1px;
  }

  .dokuwiki div.breadcrumbs div:last-child, .dokuwiki div.breadcrumbs{
    border-bottom: 1px solid #424242;
  }

  .dokuwiki div.breadcrumbs div:first-child, .dokuwiki div.breadcrumbs {
    border-top: 1px solid #424242;
  }
}
