css:
@-moz-document url-prefix("https://redmine.deriv.cloud/") {
    /* Issues */
     div.issue {
         background: #373b41;
    }
     table.list tr.issue a {
         color: #8abeb7;
    }
     img.gravatar {
         background: #282a2e;
         border-color: #282a2e;
    }
     div.flash.notice, #flash_notice {
         background: #cc6666;
    }
     div.wiki pre {
         background-color: #282a2e;
         border-color: #373b41;
    }
     ul.tagit {
         background: #373b41;
    }
    /* Issue Table */
     table.list th {
         background-color: #373b41;
    }
     table.list thead th {
         border-top-color: #b4b7b4;
         border-right-color: #b4b7b4;
         border-left-color: #b4b7b4;
         border-bottom-color: #b4b7b4;
    }
     table tr.odd {
         background-color: #282a2e;
    }
     table tr.even, table tr.even.priority-lowest, tr.odd.priority-lowest {
         background-color: #373b41;
    }
     table tr.even.priority-lowest {
         color: #b294bb;
    }
     table tr.odd.priority-lowest {
         color: #b294bb;
    }
     table.list tbody tr:hover {
         background-color: #cc6666;
         color: #ffffff;
    }
     table.list tbody tr:hover a {
         color: #c5c8c6;
    }
     table.list tbody td, table.list tbody tr:hover td {
         border: solid 1px #b4b7b4;
    }
     tr.odd.priority-lowest td, tr.even.priority-lowest td, tr.even.priority-lowest td, tr.even.priority-lowest td {
         border-color: #b4b7b4;
    }
     div#content .context-menu-selection {
         background-color: #a3685a !important;
    }
     select[multiple="multiple"] {
         background-color: #373b41;
         color: #e0e0e0;
    }
     #ajax-modal p.buttons input:hover, .ui-datepicker .ui-datepicker-buttonpane button.ui-state-hover, input[type="submit"][name="continue"]:hover, input[type="button"]:hover, .details input[type="submit"]:hover, input[type="submit"].small:hover, input[type="submit"].button-small:hover {
         background: #1d1f21;
         color: #ffffff;
    }
     #ajax-modal p.buttons input, .ui-datepicker .ui-datepicker-buttonpane button, .details input[type="submit"], input[type="button"], input[type="submit"], input[type="submit"][name="continue"], input[type="submit"].small, input[type="submit"].button-small {
         background: #373b41;
    }
    /* Body Text */
     body {
         color: #e0e0e0;
    }
    /* Header */
     #content h1, h2, h3, h4 {
         color: #ffffff;
    }
     input[type="text"], input[type="password"], fieldset#filters td.values input {
         background-color: #373b41;
         border: 1px solid #969896;
         color: #e0e0e0;
    }
     input[type="text"]:focus, input[type="password"]:focus, select:focus, textarea:focus, fieldset#filters td.values input:focus {
         border-color: #81a2be;
    }
     select {
         background-color: #373b41;
         background: none;
         color: #e0e0e0;
         border: 1px solid #969896;
    }
     select option {
         background-color: #373b41;
         color: #e0e0e0;
    }
    /* Main Background */
     html {
         background-color: #282a2e;
    }
     #content {
         background-color: #282a2e;
         border-right: none;
    }
     a, a:link, a:visited {
         color: #de935f;
    }
     #footer {
         background-color: #282a2e;
         border-top: none;
    }
     #top-menu a {
         color: #8abeb7;
    }
     p.other-formats {
         color: #969896;
    }
    /* Main Header */
     #header {
         background-color: #373b41;
         color: #c5c8c6;
    }
     #main-menu li > a {
         color: #b5bd68;
    }
    /* Sidebar */
     #main {
         background-color: #282a2e;
    }
     #sidebar h3 {
         color: #e0e0e0;
    }
     #sidebar a.selected {
         background-color: #373b41;
         color: #f0c674;
    }
    /* Calendar */
     table.cal thead th, table.cal td.week-number {
         background-color: #373b41;
    }
     table td.odd {
         background-color: #282a2e;
    }
     table td.even {
         background-color: #373b41;
         background: none;
    }
     table.cal td.today {
         background-color: #de935f;
    }
     div.tooltip:hover span.tip {
         background-color: #373b41;
         color: #e0e0e0;
    }
     table.cal td {
         border-color: #b4b7b4;
    }
     table.cal {
         border-color: #b4b7b4;
    }
    /* Wiki */
     .wiki h1, .wiki h2, .wiki h3 {
         color: #ffffff;
    }
     .wiki.wiki-page {
         color: #e0e0e0;
    }
    /* Editing */
     .box {
         background: #373b41;
         color: #e0e0e0;
    }
     textarea {
         background: #1d1f21;
         color: #e0e0e0;
    }
     input[type="text"]:focus, input[type="password"]:focus, select:focus, textarea:focus, fieldset#filters td.values input:focus {
         border-color: #81a2be;
    }
     #ajax-modal p.buttons input[type="submit"]:hover, .ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close.ui-state-hover, input[type="submit"]:hover {
         background: #8abeb7;
    }
     #ajax-modal p.buttons input[type="submit"], .ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close, input[type="submit"] {
         background: #81a2be;
    }
     ul.tagit {
         background: #282a2e !important;
    }
     ul.tagit li.tagit-new input {
         background: #373b41 !important;
    }
     #attachments_fields input.filename {
         color: #e0e0e0;
    }
     #preview fieldset {
         background: inherit;
    }
    /* Settings */
     #content .tabs ul li {
         background: #282a2e;
    }
     #content .tabs ul li a.selected {
         background-color: #b294bb;
         color: #ffffff;
    }
     #content .tabs ul li a.selected:hover, #content .tabs ul li a:hover {
         background-color: #1d1f21;
         color: #ffffff;
    }
     #content .tabs ul li a {
         background-color: #282a2e;
         color: #e0e0e0;
    }
     legend {
         color: #c5c8c6;
    }
     .nodata, .warning {
         background-color: #de935f;
         border-color: #de935f;
         color: #ffffff;
    }
    /* Date Picker */
     .ui-widget-content, .ui-datepicker .ui-state-default {
         background: #373b41;
         border-color: #969896;
         color: #e0e0e0;
    }
     .ui-datepicker .ui-state-default:hover {
         background: #1d1f21;
         color: #ffffff;
    }
     .ui-datepicker .ui-widget-header {
         background: #282a2e;
         border-color: #969896;
    }
     .ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus {
         background: #1d1f21;
         color: #ffffff;
    }
    /* Activity */
     div#activity dt .time {
         color: #c5c8c6;
    }
     div#activity dd span.description, #search-results dd span.description {
         color: #e0e0e0;
    }
     .agile-board table.list.issues-board tbody tr, .agile-board table.list.issues-board tbody tr:hover {
         background-color: #373b41;
    }
     div.flash.notice, #flash_notice {
         background: #5cc512;
    }
     #sidebar, #main {
         background: #3e454c;
    }
     a.issue.closed, a.issue.closed:link, a.issue.closed:visited {
         color: #999;
         text-decoration: line-through;
         text-decoration-color: #dd925f;
         text-decoration-line: revert;
         text-decoration-skip-ink: auto;
         text-decoration-style: solid;
         text-decoration-thickness: 2px;
    }
     tr.issue.idnt {
         background-color: #282a2e;
    }
     .syntaxhl, .syntaxhl .s1, .syntaxhl .dl {
         background: #282a2e;
    }
     .syntaxhl .na {
         color: #8bb4f7;
    }
     .check_box_group {
         background: #1d1f21;
    }
     content .box .tabs ul li a.selected {
         background-color: #282a2e;
    }
     .select2-container--default .select2-selection--multiple {
         background-color: #373b41;
    }
     .theme-Circle .select2-dropdown {
         background-color: #1d1f21;
    }
     .select2-container--default .select2-results__option--highlighted[aria-selected] {
         color: #1d1f21;
    }
     .select2-container--default .select2-selection--multiple .select2-selection__rendered li {
         color: #1d1f21;
    }
     .select2-container--default .select2-selection--multiple .select2-selection__choice__remove {
         color: #1d1f21;
    }
     #content .box .tabs ul li a.selected {
         color: #1d1f21 
    }
     div.modal {
         background: #16181a;
    }
     .ui-dialog.modal .ui-widget-header {
         color: #efefef;
         background: #16181a;
    }
     .ui-dialog .ui-dialog-titlebar-close {
         background: #16181a;
    }
     table.list:not(.odd-even) tbody tr:nth-child(even), .even, #issue-changesets div.changeset:nth-child(even) {
         background-color: #202124;
    }
     table.list:not(.odd-even) tbody tr:nth-child(odd), .odd, #issue-changesets div.changeset:nth-child(odd) {
         background-color: #282a2e;
    }
     div#version-summary {
         background-color: #202124;
    }
     div.person.details {
         background: #3e454c;
    }
     body.controller-issues.action-show .dynamicEdit {
         background: #373b41;
    }
     code {
         background-color: #282a2e !important;
         padding: 2px 4px !important;
         border-radius: 4px !important;
    }
     #wrapper {
         background: #3e454c;
    }
     #footer {
         background: #3e454c;
    }
     ::-webkit-scrollbar-track {
         -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
         background-color: #3e454c;
    }
     ::-webkit-scrollbar {
         width: 6px;
         height: 6px;
         background-color: #3e454c;
    }
     ::-webkit-scrollbar-thumb {
         background-color: #dd925f;
    }
     .issue-card.bk-lightgreen {
         background-color: #d2ffef29;
    }
     .issue-card.bk-green {
         background-color: #dfffcc29;
    }
     .issue-card.bk-orange {
         background-color: #ffdbba29;
    }
     .issue-card {
         background-color: #ffdbba29;
         backdrop-filter: blur(10px);
    }
     .issue-card p.project {
         background-color: #282a2e;
    }
     table.issues-board tbody td, table.issues-board tbody tr.issue:hover td {
         border: solid 1px #b4b7b4;
    }
     .syntaxhl .o {
         color: #dc9160;
    }
     input[type=text], input[type=date], input[type=password], input[type=search], input[type=number], fieldset#filters td.values input {
         color: #fff;
    }
    #sidebar {
         border-left: none;
    }

		tr.odd.priority-high3 {
      	background: #900;
		}
    tr.odd.priority-high3, table.list tbody tr.odd.priority-high3:hover {
				color: #fff;
    }
		tr.even.priority-high3 {
      	background: #900;
		}
    tr.even.priority-high3, table.list tbody tr.even.priority-high3:hover {
				color: #fff;
    }
}
