/* ==UserStyle==
@name           GoodReads Dark 2022
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Goodreads simple dark
@author         Paul Matthews
==/UserStyle== */

@-moz-document domain("goodreads.com") {
    
.siteHeader__logo{
	filter: brightness(300%);
}

    .siteHeader__topLine, .headroom-wrapper, .Header, .Header__contents, .SiteHeaderBanner, .mainContent , .mainContentContainer, .siteHeader__primaryNavSeparateLine, .newsHeader, .newsColumn, .tab, .tabs {
        background-color: rgb(2,2,2) !important;
    }


    body, .content, .mainContent, .mainContentFloat, .gr-homePageBody, .siteAnnouncement, .BookPage,.responsiveMainContentContainer, .gr-container-fluid, .gr-col-md-8,.gr-childNewsfeedItemContainer, .gr-newsfeedItem, .gr-newsfeedItem__details, .siteHeader__browseMenuDropdown, .primaryNavMenu__menu, .dropdown--profileMenu, .siteHeader__subNav--profile, .responsiveSiteFooter,.dropdown__container,.gcaRightContainer,.editorialCard, .amazonBadge, #box,#__next{
        background: rgb(36, 31, 31) !important;
        background-color: rgb(36, 31, 31) !important;
        
    }

    body,p,td,label,.u-defaultType, .gr-book__description, .Formatted, .description, .middleAlignedText, .quoteText, .reviewText, .Text, .value, .recommendationsWidget__justificationText, .readingChallengeStatusBox__congrats,.infoBoxRowItem, .friendName, .userReview, .updateAction, *[id^='freeTextContainerreview'], .challengeProgressHeading, .progressText, .blogWidth, .blogWidth, strong, .js-dataTooltip, .bookDescription, .gcaBookDescription{
        color:rgb(168, 152, 152) !important;
    }

    h1,h2,h3,h4, .gcaMastheader, .Text__title1, .bannerYearText, .yyibHeader, .yyibBooksLockup, .yyibAverageLength, .yyibCrowdFavoriteHeading {
        color: blanchedalmond !important;
    }

    .gr-hyperlink, a, .Button__labelItem, .SocialSignalsSection__caption{
        color:beige !important;
    }
    
    .viewBooksLink{
        color:#666  !important;
    }
    
    .gr-button--quiet{
        background-color: rgb(160,120,80)
    }

    #discoveryBox{
        background-color:rgb(56, 56, 45) !important;
    }

    .gr-childNewsfeedItem__header {
        background-color:rgb(3,3,3);
    }
    
    .TruncatedContent__gradientOverlay{
        background:linear-gradient(to bottom,rgba(255,255,255,0),rgb(36, 31, 31) 1.6rem)
        
    }
}
