css:
@-moz-document domain("www.oxfordlearnersdictionaries.com"), domain("www.oxfordlearnersdictionaries.com") {
#ad_leftslot,
#topslot_container,
.parallax-container,
#ring-links-box,
#ad_btmslot,
#h-redeem, 
#h-upgrade
{
  display: none;
}


#ad_leftslot_container {
  opacity: 0;
  width: 80px;
}

@media screen and (min-width: 1310px) {
  .responsive_container.xamerican_english #ad_leftslot_container, 
  .responsive_container.xenglish #ad_leftslot_container,
  .responsive_container.xreference #ad_leftslot_container {
    opacity: 0;
    width: 150px;
  }
}
 
#rightcolumn .responsive_row {
  display: block !important;
}

#searchbar {
  margin-bottom: 20px;
}
}
