css:
@-moz-document url-prefix("https://glimesh.tv/") {
body {
  overflow: hidden;
}
.navbar {
  display: none;
}
.container-stream .row {
  margin-top: 5px !important;
}
.container-stream .layout-spacing {
  padding-bottom: 10px;
}
#video-column {
  max-height: 100%;
  height: 100vh;
  padding-left: 5px;
  padding-right: 2.5px;
  flex: 0 0 80%;
  max-width: 80%;
}
#video-column .card-footer {
  display:none !important;
}
#video-column .card-header  {
  border-bottom: none;
  background-color: none;
  position: absolute;
  top: 5px;
  left:10px;
  width: 100%;
  padding-right:20px !important;
  z-index: 20;
  animation: slide-out 1.5s forwards;
  animation-delay:2s;
}

#video-column:hover .card-header {
  animation: slide-in 1.5s forwards;
}

@keyframes slide-in {
  0% { top: -100px}
    100% { top: 5px }
}
@keyframes slide-out {
  0% { top:5px;}
  100% { top:-100px;}
}
#chat-column {
  padding-left: 2.5px;
  padding-right: 5px;
  flex: 0 0 20%;
  max-width: 20%;
}
#chat-column #chat {
  max-height: calc(100vh);
}
}
