css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(forums.overclockers.co.uk) {

/***** Header logo area *****/

body > table > tbody > tr > td:first-child {text-align:left!important;padding:0 30px!important;}
body > table {background:#556C5F!important}
body > a + table[border="0"][width="100%"][cellpadding="0"][cellspacing="0"][align="center"]
{border-bottom:10px solid #728776!important}

img[src$="forumlogo_final4.jpg"] {background: url('data:image/jpg;base64,') no-repeat 0 -6px!important;width:0;height: 0;padding: 90px 0 0 180px;}

/***** Main body and set font *****/

body {background-color:#728776!important;color:#000!important;margin:0!important;padding:0 0 10px 0!important;}
html,body,table,div,tr,td,tbody,span,fieldset,legend,a,strong {font-family: Tahoma;color:#000!important}

/***** Links *****/

a:link, body_alink{color: #556C5F!important}
a:visited, body_avisited{color: #556C5F!important}
a:hover, a:active, body_ahover{color: #000!important}
a.bigusername {font-weight:bold!important;color:#556C5F!important;background:none!important;}
a[href*="member.php?find=lastposter"]{color:#556C5F!important;text-decoration:none!important;font-weight:normal;}

/***** Redefine some existing CSS *****/

option,select {background-color:#fff!important;color:#000!important;}
.alt2Active {background:#060!important;}
.alt1Active {background:#D6DED4!important;}
.panel {background-color:transparent!important;border:0!important}
.panelsurround {background:#556C5F!important;}
.smallfont {color:#000!important}
.vbmenu_popup {border:0!important;}
.vbmenu_option {background:#fff!important;}
.tcat {color:#000!important}
.thead {background-color:#D6DED4!important;border-top:0!important;border-color:#556C5F!important;white-space: nowrap;color:#111!important}
.tfoot {background:#D6DED4!important;text-align:center!important;}
.vbmenu_popup {padding:0!important;background:#444!important;}
.page {background-color:#728776!important;padding:0!important;z-index:2!important}
.imagebutton {background-color:transparent!important;}
.imagebutton:hover {background-color:#fff!important;}
.smallfont > em {float:right!important;font-style:normal!important;text-decoration:underline}
.normal {color:#060!important;}
.alt1 {background-color:#D6DED4!important;border-color:#556C5F!important;color:#000!important}
.alt2 {background-color:#D6DED4!important;border-color:#556C5F!important;color:#000!important}
.alt1Active .smallfont font[color="#ffff00"] {color:#030!important;font-weight:bold!important}
.vbmenu_control, 
.vbmenu_hilite, 
.tcat {background:#C6D3C2!important;}
.tborder {background-color:transparent!important;background-image:none!important}
.time {color:#000!important;font-weight:bold!important;padding:0!important}
.popup_feedback, #fontOut, #sizeOut {background-color:#ccc!important;color:#000!important;}

img, img:link {max-width:600px!important}
input.bginput,input.button {color:#000!important;padding:2px!important;}

.wysiwyg {background-color:#fff!important;width:auto!important;padding:10px!important}

fieldset legend, fieldset td, fieldset a:link, fieldset a:visited,
fieldset, .fieldset{color:#fff!important}

#threadslist tr td+td+td+td.alt2 .inlineimg {height:12px!important}

div > strong + a[href^="showthread.php?p="] {text-decoration:none!important}
div > strong + a[href^="showthread.php?p="]::after {color:#000!important;content:"\2730";font-size:13px!important}

/***** Navigation bits *****/

div.navbord {position:absolute!important;top:72px;right:10px!important;border:0!important}
div.navbord table {width:auto!important;border:0!important;border-spacing:0!important}
div.navbord table td {display:inline!important;margin:2px!important;-moz-border-radius:3px 3px 0 0!important}
div.navbord table td:hover {opacity:0.7!important;}
div.navbord table td+td+td+td+td {display:none!important;}
div.navbord table td+td+td+td+td+td {display:inline!important;}

/***** Search bits *****/

#navbar_search_menu {display:inline!important;position:absolute!important;top:0!important;float:right!important;right:10px!important;z-index:4!important}
#navbar_search_menu table {text-align:right!important;width:100px!important}
#navbar_search_menu table tr,#navbar_search_menu table tr+tr+tr {display:none!important}
#navbar_search_menu table tr+tr,#navbar_search_menu table td {display:inline!important;background-color:transparent!important}
#navbar_search_menu {background-color:transparent!important}
#navbar_search_menu input.bginput {margin:0 10px 0 0!important}
#navbar_search_menu label {color:#fff!important}
#navbar_search_menu form > div:not([style])::before {content:"Search forum";padding:5px;color:#fff}

/***** Hide banners,seperator,linebreaks,hr,some images etc *****/

td#header_right_cell, div > div.page > div > br,
img[src*="images/editor/separator.gif"],
table[id^="post"] td.alt2 > div[id^="postmenu_"]+.smallfont+.smallfont br,
table[id^="post"] tr+tr+tr td hr,
table[id^="post"] tr+tr+tr td+td a img,
div > strong+a[href^="showthread.php?p="] img,
td[id^="td_post_"] hr
{display:none!important}

/***** Div that holds quotes, code or php boxes *****/
  
  div[style="margin: 5px 20px 20px;"] {margin:4px 0 4px 0!important}

  /***** [CODE] box *****/

  pre.alt2 {
    height:auto!important;width:auto!important;max-width:800px!important;
    border:1px solid #fff!important;-moz-border-radius:4px!important;background-color:transparent!important;
    background-image:url('data:image/jpg;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABZ0RVh0Q3JlYXRpb24gVGltZQAwMS8wNi8wOeQdC6wAAAAYdEVYdFNvZnR3YXJlAEFkb2JlIEZpcmV3b3Jrc0+zH04AAAANSURBVAiZYwjNid8BAAQTAdnXXp5/AAAAAElFTkSuQmCC')!important;}

  /***** [QUOTE] box *****/
td.alt2[style="border:1px inset"]  {
    border:1px solid #fff!important;background-color:transparent!important;
    color:#000!important;-moz-border-radius:4px!important;margin:2px!important;
    background-image:url('data:image/jpg;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABZ0RVh0Q3JlYXRpb24gVGltZQAwMS8wNi8wOeQdC6wAAAAYdEVYdFNvZnR3YXJlAEFkb2JlIEZpcmV3b3Jrc0+zH04AAAANSURBVAiZYwjNid8BAAQTAdnXXp5/AAAAAElFTkSuQmCC')!important;}

   /* Links inside quotes */
   
  td[class="alt2"][style="border: 1px inset ;"] a {color:#000!important;font-style:normal!important;}

  /***** [PHP] box *****/

  div[style="margin: 5px 20px 20px;"] div.alt2 {
    background-color:#fff!important;-moz-border-radius:4px!important;
    border:1px solid #ededed!important}

  /***** [CODE],[QUOTE], and [PHP] title *****/

  div.smallfont[style="margin-bottom:2px"] {
    -moz-border-radius:3px 3px 0 0!important;
    width:80px!important;margin:0 auto 0 4px!important;
    border:0!important;background:#fff;
    text-align:center!important;padding:2px 4px!important}

/***** Style today in calendar *****/

  td[title="Today"] {border: 2px outset #000!important;background:#fff!important}

/***** Style some bits in the UserCP area *****/

#content {background:#D6DED4!important;margin:1px!important;padding:5px!important}
.tab_list {margin-top:50px!important}
.tab_list li {vertical-align:bottom!important;padding:4px 2px !important; background:#728776!important;text-align:center}


/***** Various bits for page that displays the thread and posts ('showthread.php') *****/

.tborder[id^="post"] td.thead {background-color:#C6D3C2!important;border-bottom:1px solid #556C5F!important}
.tborder[id^="post"] td.thead:first-child {background-color:#C6D3C2!important;border-right:1px solid #556C5F!important}
.tborder[id^="post"] td.alt2 > div[id^="postmenu_"]+.smallfont+.smallfont > a > img {height:auto!important;width:65px!important;margin:3px 0 0 -4px}
.tborder[id^="post"] tr+tr+tr+tr td {border-top:0!important;}
.tborder[id^="post"] tr+tr+tr td {border-top:1px dashed #556C5F!important;}
.tborder[id^="post"] tr+tr+tr a {text-decoration:none!important}
.tborder[id^="post"] tr+tr+tr td.alt2 a:after {content:"Report";font-size:9px}

pre.alt2,
.tborder[id^="post"] tr+tr+tr td,
.tborder[id^="post"] td[id^="td_post_"],
.tborder[id^="post"] td[id^="td_post_"] div[id*="post_message"]
{
  background-color:transparent!important;
  background-image:url('data:image/jpg;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABZ0RVh0Q3JlYXRpb24gVGltZQAwMS8wNi8wOeQdC6wAAAAYdEVYdFNvZnR3YXJlAEFkb2JlIEZpcmV3b3Jrc0+zH04AAAANSURBVAiZY7h270oDAAgfAwmCDOwgAAAAAElFTkSuQmCC')!important;
}

td[id^="td_post_"] {padding:8px!important}

 /**   Style the div that holds the post text 
  **/

   div[id*="post_message"] {min-height:90px!important;padding:5px 0!important;margin:5px 0 0 0!important;}

 /**   If post contains a title show a line using a border 
  **/

  .smallfont + hr + div[id*="post_message"] {border-top:1px solid #556C5F!important}

img[src*="images/statusicon/user_"] {float:left!important;margin:3px 3px 0 0!important}

div[id^="edit"] {margin:10px 1px 8px 6px !important;background:#556C5F!important;}
div[id^="edit"] table.tborder {
  margin:-4px -12px 0 -6px!important;
  background-position:right 26px!important;
  background-color:#D6DED4!important;
  background-repeat:no-repeat!important;
  background-image:url('http://img158.imageshack.us/img158/807/deviantmas3vo9.gif')!important;}

.alt2[rowspan="2"][style="border-style: none solid; border-color: -moz-use-text-color rgb(0, 0, 0); border-width: 0px 1px;"][width="175"] {width:120px!important;overflow:auto!important;}
.alt2[rowspan="2"][style="border-style: none solid; border-color: -moz-use-text-color rgb(0, 0, 0); border-width: 0px 1px;"][width="175"] div {text-align:center!important;overflow:auto!important;font-size:11px!important}
.alt2[rowspan="2"][style="border-style: none solid; border-color: -moz-use-text-color rgb(0, 0, 0); border-width: 0px 1px;"][width="175"] div+div+div div{text-align:left!important;}

.page > div > div.smallfont {font-size:0!important}
.page > div > div.smallfont a {text-decoration:none!important;color:#fff!important;padding:5px!important;}
.page > div > div.smallfont a::before {content:"\25C0 \ Previous";font-size:12px!important;}
.page > div > div.smallfont a+a::before {content:""}
.page > div > div.smallfont a+a::after {content:"Next \25BA";font-size:12px!important;}

/***** Create buttons, trust, edit, quote etc.... *****/

.tborder[id^="post"] a[target="ocuk_trust"]::before,
.tborder[id^="post"] a[target="ocuk_trust"]+a::before,
.tborder[id^="post"] a[target="ocuk_trust"]+a+a::before, 
.tborder[id^="post"] a[target="ocuk_trust"]+a+a+a::before,
.tborder[id^="post"] a[target="ocuk_trust"]+img+a::before,
.tborder[id^="post"] a[target="ocuk_trust"]+img+a+a::before,
.tborder[id^="post"] a[target="ocuk_trust"]+img+a+a+a::before,
.tborder[id^="post"] a[target="ocuk_trust"]+img+a+a+a+a::before
{
  padding:3px 7px!important;color:#D6DED4!important;
  -moz-border-radius:4px!important;font-size:11px!important;
  background-color:transparent!important;
  background-image:url('data:image/jpg;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAYAAAAfFcSJAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEgAACxIB0t1+/AAAABZ0RVh0Q3JlYXRpb24gVGltZQAwMS8wNi8wOeQdC6wAAAAYdEVYdFNvZnR3YXJlAEFkb2JlIEZpcmV3b3Jrc0+zH04AAAANSURBVAiZYwjNid8BAAQTAdnXXp5/AAAAAElFTkSuQmCC')!important;
}

.tborder[id^="post"] a[target="ocuk_trust"]::before {content:"Trust";}
.tborder[id^="post"] a[target="ocuk_trust"]+a::before {content:"Quote";}
.tborder[id^="post"] a[target="ocuk_trust"]+a+a::before {content:"Multi Quote";}
.tborder[id^="post"] a[target="ocuk_trust"]+a+a+a::before {content:"Quick Reply";}
.tborder[id^="post"] a[target="ocuk_trust"]+img+a::before {content:"Edit";}
.tborder[id^="post"] a[target="ocuk_trust"]+img+a+a::before {content:"Quote";}
.tborder[id^="post"] a[target="ocuk_trust"]+img+a+a+a::before {content:"Multi Quote";}
.tborder[id^="post"] a[target="ocuk_trust"]+img+a+a+a+a::before {content:"Quick Reply";}

img[src="images/buttons/threadclosed.gif"] {margin:2px 0 0 1px!important;height:0!important;padding:24px 148px 0 0!important;float:left!important;
background-image:url('data:image/gif;base64,');background-position:left center;background-repeat:no-repeat}

img[title="Reply"] {margin:2px 0 0 1px!important;height:0!important;padding:24px 53px 0 0!important;
background-image:url('data:image/gif;base64,');}

img[title="Post New Thread"] {margin:2px 0 0 1px!important;height:0!important;padding:24px 100px 0 0!important;
background-image:url('data:image/gif;base64,');}

img[src="images/buttons/collapse_tcat.gif"] {height:0!important;padding:16px 16px 0 0!important;
background-image:url('data:image/gif;base64,');}

img[src="images/buttons/collapse_tcat_collapsed.gif"] {height:0!important;padding:16px 16px 0 0!important;
background-image:url('data:image/gif;base64,');}

/***** Style page when error shown, such as Database error *****/

body > table:only-child#container {border-left:10px solid #556C5F!important;border-right:10px solid #556C5F!important;padding:10px!important;background-position:right 6px!important;background-color:#D6DED4!important;background-repeat:no-repeat!important;background-image:url('http://img158.imageshack.us/img158/807/deviantmas3vo9.gif')!important;width:100%!important}
body > table:only-child#container td {padding:0!important;display:block!important;text-align:left!important;}
body > table:only-child#container img {display:none!important}
body > table:only-child#container * {font-size:14px!important;font-family:Georgia!important;list-style-type:decimal!important}

}

@-moz-document url-prefix("http://forums.overclockers.co.uk/showpost.php?p=") {

/***** Fix table background on view single post *****/

.tborder[id^="post"] tr+tr td {background-color:#D6DED4!important}

}
