css:
@-moz-document domain(old-games.ru) {
body {
  border: 1px solid #aaa;
  margin: 10px auto;
  background: #f2f2f2;
}
a:hover {
  text-decoration: none;
  opacity: 0.5;
}
a {
  transition: all 0.3s;
}

.leftlist a, td a {
  color: black !important;
}
.leftlist li{
  margin-bottom: -1px ; /* border fix*/
}
li a:hover, td a:hover {
	text-decoration: none !important;
  opacity: 100;
}
.button_clicked .whitelinkclicked {
  font-weight: 400;
  background: lightgray;
}
.right,.left,.tiptext {
  border: 1px solid #316DBE;
  padding: 5px;
  margin: 0px 5px 5px 5px;
  background: #fff;
}

.listtable {
 width: 865px;
  float: none !important;
  margin-left: -11px;
}
#lside, #rside {
  width: 235px;
  background: none;
	border: 0px;
}
.main {
  border-bottom: 0px;
}
#main  {
  overflow: hidden;
  margin-top: 7px;
  margin-bottom: 15px;
  border-top: 1px #aaa solid;
  border-bottom: 1px #aaa solid;
}
.breadcrumb {
  padding: 6px 0px 6px 15px;
  vertical-align: center;
  border-bottom: 1px #aaa solid;
  }
#rside img {
	max-width: 100%;  
}


.random-game {
  height: 210px;
}
#random_screen_game {
	float:  none !important;
  margin: 0 auto;
}
.catalog_header {
  	height: 35px;
}
.bluetitle {
  background: #316DBE;
  padding: 8px 0px;
  border: 0px;
  margin: 7px 5px -1px 5px;
}
.head {
  color: #222222;
  font-weight: 200;
  text-align: center;
  margin-bottom: 5px;
	text-transform: lowercase;
}
.head:first-letter {
text-transform: capitalize;
}
.footwarn  {
  background: #222;
  border-top: 7px solid #DC143C;
}
.footwarn  a {
  color: #585858 !important;
}
.footwarn  a:hover {
  color: #959595 !important;
}
.socialbutton a{
  color: white !important;
}
.socialbutton a:hover {
  color: white !important;
}
.footwarn h3 {
  color: #959595;
}
.footwarn div {
  color: #585858;
}
 .pager {
   padding-top: 10px !important;
   max-width: 395px;
   margin: 0 auto !important;
}
.showmore {
  min-width: 110px !important;
  padding: 3px;
}
div .showmore a{
  padding: 5px 0px 7px 0px;
}
}
