css:
@-moz-document domain("ffxiclopedia.fandom.com") {
  .page__main, .fandom-sticky-header, .wds-list, .portable-infobox, .WikiaLightbox
{
  background-color: #323232;
}

tr > th, div, span, i, h2, #mw-toc-heading, .toctogglespan::before, 
.toctogglespan::after, figcaption, .caption
{
  color: white;
}

td, th, .portal__welcome__text > *, .link-text, .plainlinks
{
  color: black;
}

td > b, h2 + p, h2 + ul, h2 + ul li, h3 + p, 
h3 + p + p, p + dl, p + ul, p + ul + ul, 
h3 + p + ol, h3 + p + ol > li {
  color: white;
}

#mixed-content-footer 
{
  display: none;
}

.page__right-rail
{
  display: none;
}

.global-navigation, .global-footer, .notifications-placeholder {
  display: none
}

span::after {
  color: white;
}

.tocnumber {
  color: white !important;
}

/* unvisited link */
a:link, .wds-button.wds-is-text, .toctogglelabel::after, #ca-history, #ca-talk, 
.text, .category-page__alphabet-shortcut > a:link
{
  color: #d77e44;
}

ul > li > a:link
{
  color: #d77e44 !important;
}

/* visited link */
a:visited, .text:visited{
  color: #7c7ccc;
}

/* mouse over link */
a:hover, .wds-button.wds-is-text:hover, .text:hover {
  color: #9c9ccc;
}

/* selected link */
a:active {
  color: #ffd7a7;
}

/* mouse over table link */
table a:hover {
  color: hotpink;
}

/* selected table link */
table a:active {
  color: lightcyan;
}

.infobox-quest-header, .infobox-quest-header > *
{
  color: black;
}

.render-wiki-recommendations  *
{
  color: white !important;
}

.render-wiki-recommendations  a:link
{
  color: white !important;
}

body::-webkit-scrollbar {
    width: 0.85em;
}

body::-webkit-scrollbar-track {
    background-color: #3f3f3f;
    box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
}

body::-webkit-scrollbar-thumb {
    background-color: #856741;
    outline: 1px solid #fdb44d;
}
}
