@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://), url-prefix(https://) {

/* GOODBYE ADS */

#mbEnd, #rhsline.std, #pa1, #tads, #tads.c, .tas,
#ImgCont > TABLE[style*='background: rgb(255, 248, 221)'], #mclip,
.ads.noprint, #rv_panel, .serp TD.right-nav, .ad, ad, ads, .ads, #ad,
.serp TD#table-spacer-cell, #ads-rhs, .pY, .u5, .u8,
#lhid_search_ad_unit, #rhsa, #rhswrapper > TABLE#rhssection TR,
 BODY[onunload='google.Quote.stopQuotes();'] DIV[class='sfe-break-bottom-16'], 
.ads.topads, #rhsads, [src*='ads.*'], [src*='ads.gigaom.com'],
.item-body div p[align='right'], [href*='www.google.com/adsense'],
#ads-top, #ads-bot, [href*='doubleclick.net'], [href*='rad.msn.com']

{display:none !important;}

object, embed, #ads, #ssponsor, .google_ads { display: none !important; }


}



@-moz-document domain(google.com), url-prefix( http://www.google.) {

table[style=""][align="right"],    /* froogle */
div[ads],                          /* google  */
div[id^=tpa],                      /* google  */
table[id=mbEnd],                   /* google  */
div[spl],                          /* google  */
table[width="25%"][align="right"], /* google  */
table[class=lads],                 /* google  */
div[class="rh"]                    /* mail    */

{display: none !important;}

}


/* GOODBYE ADS from Google Reader */

@-moz-document url-prefix(http://), url-prefix(https://www.google.com/reader/) {

#ad, #adzbanner, *[id^='ad-'], *[id^='ad_'], *[id$='_ad'], *[id*='_adspace'], *[id*='_ad_'], *[id*='_ads_'],
.ad, *[class^='ad_']:not([class^='ad_redirect_']), *[class$='_ad'],
*[src*='/adserving.'], *[src*='&ad_size'],  *[src*='&ad_frame'], *[src*='bannerid'],
*[src *='/ad.'], *[src *='/pagead2.'], *[src *='/ads.'], *[src *='/ads/'], *[src *='/googleads.'], *[src *='/feedads.'],
*[href*='/ad.'], *[href*='/pagead2.'], *[href*='/ads.'], *[href*='/ads/'], *[href*='/googleads.'], *[href*='/feedads.']

{ display: none !important; }

}


/* GOODBYE ADS from ORKUT */

@-moz-document domain("orkut.com.br"), domain("orkut.com") {

div#rhs_ads {display:none !important;}
div#adv {display:none !important;}
div#top_ads {display:none !important;}
div.G15r46myFF {display:none !important;}
div.Gyi8qmpCG {display:none !important;}

}
