css:
@-moz-document domain(old-games.ru) {
body {
  box-shadow: 0px 5px 10px 2px rgba(34, 60, 80, 0.2);
}
.main {
  margin-top: 10px;
  border-bottom: 0px;
}
.imageWrapper {
  border: 0px !important;
}
/* links */
a {
  transition: all 0.3s !important;
}
a:hover {
  text-decoration: none !important;
  opacity: 0.7;
}
.headerpanel a {
  transition: all 0s !important;
}
.headerpanel a:hover {
  opacity: 1 !important;
	text-decoration: none;
}
.news-element a:visited, .right a:visited {
  color: #1c58a9;
}
.main a:hover {
  text-decoration: none !important;
}
#pageFooter a {
  font-size: 11px;
  text-transform: uppercase;
}
#pageFooter a:hover {
  color: #dc1407;
  text-decoration: none;
  padding-left: 10px;
}
.tabs li.current, .tabs li.current:hover {
  background: gray;
}
/* end links */
#main {
  background: #fff;	
  border: 0px solid #fff;
  border-top: 0px;
  box-shadow: 0px 5px 10px 2px rgba(34, 60, 80, 0.2);
  margin-bottom: 20px;
}
.breadcrumb {
  padding: 5px 0px 4px 10px;
  margin-top: 0px;
}
#lside, #rside {
  background: #fff;
  padding: 0px;
  border: 0px;
  width: 200px;
  }
#rside img {
	max-width: 100%;  
}

/* footer */
#pageFooter {
  background: #242424;
}
.smallcolumn h3 {
  color: #555;
  text-transform: uppercase;
  border-bottom: 1px solid #dc1407;
  margin-right: 10px;;
  padding: 5px 0px 5px 0px;
}
.bigcolumn, .smallcolumn a {
  color: #555;
}
.socialbutton {
  opacity: 0.5;
  padding: 5px 0px 5px 0px;
  border-radius: 10px;
  transition: 0.5s;
}
.socialbutton:hover {
  opacity: 0.8;
  margin-left: 10px;
}
.socialbutton a:hover {
  color: white !important;
}
/* end footer*/
.bluetitle {
  font-size: 12px;
  border: 0px;
  padding: 7px;
  margin: 0px 0px 5px 0px;
  text-transform: uppercase;
  font-weight: 200;
}
.head {
  text-transform: uppercase;
  font-weight: 200;
  text-align: center;
  margin-bottom: 8px;
}
.tiptext {
  padding: 5px 0px 5px 0px;
}
ul.leftlist li {
  margin: -1px;
  transition: 0.3s;
}
.game_comments_row {
  margin: 5px 5px 5px 15px;
}
.bgstripe2, .bgstripe1 {
  border-left: 3px solid lightblue;
  margin-bottom: 5px;
}
/* buttons */
div.showmore a {
  padding: 5px;
  border-radius: 5px;
  text-transform:  uppercase;
  font-weight: 200;
}
div.showmore {
  margin-bottom: 3px;
}
/* game_screen fix */
.random-game {
  height: 210px;
}
#random_screen_game {
	float:  none !important;
  margin: 0 auto;
}
}
