@namespace url(http://www.w3.org/1999/xhtml);


/* PHOTOBUCKET STRIPPED */
@-moz-document domain("photobucket.com") {

td, #panelFeatures, #containerMain, #panelHomepageSearch, #cellAd, #panelFooter, img { display: none !important; }


#containerThumbnails, #cellUpload, #navUserMenu, .containerPagination, .containerHeader, #containerThumbnails li a img 

{ display: inline !important; }

span, ul#menuNavTabs li, .breadcrumbLinkCurrent, .breadcrumb, #linkBuyPrints { background: transparent !important;}

div, li, td, tr, ul, table { padding: 1px !important; margin-left: auto !important; margin-right: auto !important; border: 0px !important;}


{ color: #ada089 !important; background: none !important; background-color: black !important; }

a:link { color: #738639 !important }
a:visited { color: #738639 !important } 
a:hover { color: #86b324 !important }
a:active { color: #738639 !important }

input[type=button], input[type=submit], input[type=reset], input[type=text], input[type=password], input[type=file], input 

{ background-color: #0F0F0F !important; color: white !important;  border: 1px solid #0F0F0F !important;}



}


/* PHOTOBUCKET FIX (07) */

@-moz-document domain("photobucket.com") {

div#cellAd,span.breadcrumbLabel,div#collapsable-ad-id-PROMO, span#containerAlbumPartnerLinksBC, td#advPanelContainer, td#cellTopSearches_FooterSearch, td#cellImageCounter_FooterSearch, div#panelFooterSearch, div#topNavBar, div#panelSubAlbums, div#containerBreadcrumb, a#tab_mobile, td#panelAlbumLeft, td#navTabs, iframe#ad, div#containerPitch, div#panelAbout, div#panelAlbumLeft, div#panelAnnouncements, div#lbContent, div#overlay, div#containerSqAd, td#cellPromo, div#panelFeaturedMedia {

display: none !important;

}


table#tblUpload, div#albumErrorPanel { width:410px !important;
}


}
