css:
@-moz-document url-prefix("https://forum.truckersmp.com")
{
/**
  * TruckersMP Forum Blue Accent (Dark Theme)
  * Created by Digital (https://forum.truckersmp.com/index.php?/profile/4963-digital); edited by Joschi (https://forum.truckersmp.com/index.php?/profile/222408-joschi/)
  */

  .ipsApp .ipsButton_important
  {
    background: #3498DB none repeat scroll 0 0;
  } 
  .ipsApp .ipsButton_normal
  {
    background-color: #3498DB;
  } 
  .ipsBadge_positive
  {
  --badge--color: #3498DB;
  } 
  .cAnnouncementIcon
  {
    background-color: #3498DB;
  } 
  :root
  {
  --positive-dark: #217dbb;
  --theme-brand_primary: 52, 152, 219;
  --theme-featured: 52, 152, 219;
  --theme-comment_count: 52, 152, 219;
  --theme-mentions: 33, 125, 187;
  --theme-primary_button: 52, 152, 219;
  --theme-notification_bubble: 52, 152, 219;
  } 
  .ipsRepBadge_positive,
.ipsRepBadge_positive:hover
  {
    color: #EEEEEE!important;
  }
  .fa-plus-circle:before
  {
    content: "\f055";
    color: #3498DB;
  }
  .fa-comment:before
  {
    content: "\f075";
    color: #3498DB;
  }
  .fa-check-circle:before
  {
    content: "\f058";
    color: #3498DB;
  }
  .fa-circle:before
  {
    content: "\f111";
    color: #3498DB;
  }
  .ipsDataItem_unread>a,
.ipsDataItem_unread>a>span
  {
    color: #EEEEEE!important;
  }
  .ipsNavBar_primary>ul>li>a:hover
  {
    border-bottom: 1px solid #3498DB;
  }
  .cProfileRepScore_positive
  {
    background-color: rgb(52 152 219/0.5);
  }
  .ipsOnlineStatus,
.ipsOnlineStatus_online
  {
    color: #3498DB;
  }
}
