css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theatlasphere.com")
{
    /***************************/
    /***** OLD PAGE STYLES *****/
    /***************************/
    #columns-content, #columns-content p, #columns-content blockquote
    {
        font-size:      18px !important;
        line-height:    150% !important;
    }

    body div table
    {
        width:          95% !important;
    }

    body div table td
    {
        width:          165px !important;
    }
    body div table td+td
    {
        width:          auto !important;
    }
    body div table td+td+td
    {
        width:          165px !important;
    }

    table.content-frame
    {
        width:          100% !important;
    }

    .letter
    {
        font-size:      20px;
        line-height:    150%;
    }

    .Apple-style-span
    {
        font-size:      18px !important;
        line-height:    150% !important;
    }

   .Apple-style-span span
    {
        font-size:      18px !important;
        line-height:    150% !important;
    }

    .columns-summary-full
    {
        font:           18px verdana !important;
    }


    /***************************/
    /***** NEW PAGE STYLES *****/
    /***************************/
    #page #content
    {
        font-size:      20px !important;
        line-height:    1.5 !important;
        width:          69% !important;
    }

    #header 
    {
        overflow:       hidden !important;
        width:          100% !important;
    }

    #content.columns div.summary
    {
        font-size:      20px !important;
        line-height:    1.5 !important;
    }

    #content.columns div.ratings
    {
        font-size:      20px !important;
    }

    #content.columns div.byline
    {
        font-size:      20px !important;
    }

    .container
    {
        margin:         0 10px !important;
        width:          99% !important;
    }

    #page #left.sidebar
    {
        width:          140px !important;
    }

    #page #right.sidebar
    {
        width:          10% !important;
    }

    /* Very faulty markup! */
    #content #content
    {
        width:          100% !important;
    }

    #feedback textarea
    {
        font:           20px verdana !important;
        height:         10em !important;
        width:          96% !important;
    }


    #side-login
    {
        height:         auto !important;
        padding:        10px !important;
        width:          99% !important;
    }

    #side-login input.fields
    {
        width:          100% !important;
    }
}
