css:
@-moz-document domain(old-games.ru) {
html {
  background: #101010;
}
body {
  box-shadow: 0px 5px 10px 2px #000;
  background: #292929;
}
.header {
  opacity: 0.1;
}
.main {
  margin-top: 10px;
  border-bottom: 0px;
  color: #757575 !important;
}
.imageWrapper {
  border: 0px !important;
}
input[type="text"], #quicksearchform{
  background: #202020;
  border: 0px;
  color: #757575;
}
/* links */
a, .game_title {
  color: #126267 !important;
  transition: all 0.3s !important;
}
a:hover {
  text-decoration: none !important;
  opacity: 0.7;
}
a:visited {
  color: #757575;
}
.headerpanel a {
  transition: all 0s !important;
}
.headerpanel a:hover {
  opacity: 1 !important;
	text-decoration: none;
}
.news-element a:visited, .right a:visited {
  color: #1c58a9;
}
.main a:hover {
  text-decoration: none !important;
}
#pageFooter a {
  font-size: 11px;
  text-transform: uppercase;
}
#pageFooter a:hover {
  color: #dc1407;
  text-decoration: none;
  padding-left: 10px;
}
.tabs li.current, .tabs li.current:hover {
  background: gray;
}
/* end links */
#main {
  background: #292929;	
  border: 0px solid #fff;
  border-top: 0px;
  box-shadow: 0px 5px 10px 2px #202020;
  margin-bottom: 20px;
  color: #757575 !important;
}
.title, .news-title {
  color: #126267;
  text-transform: uppercase;
  font-weight: 200;
  font-size: 14px;
}
.news-title {
  text-transform: initial !important;
}
.breadcrumb {
  background: #161616;
  padding: 5px 0px 4px 10px;
  margin-top: 0px;
  color: #757575 !important;
}
.active-link {
  opacity: 0.7;
}
#lside, #rside {
  background: #292929;
  padding: 0px;
  border: 0px;
  width: 200px;
}
#lside img {
  display: none;
}
/* footer */
#pageFooter, textarea  {
  background: #242424;
  border: 0px;
  padding: 5px;
}
.smallcolumn h3 {
  color: #555;
  text-transform: uppercase;
  border-bottom: 1px solid  #126267;
  margin-right: 10px;;
  padding: 5px 0px 5px 0px;
}
.bigcolumn, .smallcolumn a {
  color: #555;
}
.socialbutton  a {
  color: #fff !important;
}
.socialbutton {
  opacity: 0.5;
  padding: 5px 0px 5px 0px;
  border-radius: 10px;
  transition: 0.5s;
}
.socialbutton:hover {
  opacity: 0.8;
  margin-left: 10px;
}
.socialbutton a:hover {
  color: white !important;
}
/* end footer*/
.bluetitle {
  background: #161616;
  font-size: 12px;
  border: 0px;
  padding: 7px;
  margin: 0px 0px 5px 0px;
  text-transform: uppercase;
  font-weight: 200;
  color: #757575;
}
.head {
  text-transform: uppercase;
  font-weight: 200;
  text-align: center;
  margin-bottom: 8px;
  color: #757575;
}
.tiptext {
  padding: 5px 0px 10px 0px;
  color: #757575;
}
ul.leftlist li {
  margin: -1px;
  transition: 0.3s;
}
.game_comments_row {
  margin: 5px 5px 5px 15px;
}
.bgstripe2, .bgstripe1 {
  border-left: 3px solid #0b3a3d;
  margin-bottom: 5px;
}
#main .red {
  color: #707070;
}
/* buttons */
div.showmore a, .news-more, .file-properties-item, input[type="submit"] {
  background: #161616;
  padding: 5px;
  border-radius: 5px;
  text-transform:  uppercase;
  font-weight: 200;
  border: none;
  color: #757575;
  transition: 0.5s all;
}
div.showmore {
  margin-bottom: 3px;
}
div.showmore a:hover, .news-more:hover, .file-properties-item:hover, input[type="submit"]:hover {
    box-shadow: 0px 2px 5px 1px #000;
  	cursor: pointer;
}
.file-properties-item {
  background: #292929;
}
/* game_screen fix */
.random-game {
  height: 210px;
}
#random_screen_game {
	float:  none !important;
  margin: 0 auto;
}

#lside table {
  background: none;
}
ul.leftlist li, #lside td {
  background: #161616 !important;
  margin: 1px;
  border: none;
  border-radius: 5px;
}
ul.leftlist li:hover, #lside td:hover {
  opacity: 0.7;
}
/*table*/
.listtable {
  border: none !important;
}
.bgstripe1 {
  background: #292929;
}
.bgstripe2, #main th {
  background: #202020;
}
tr {
  transition: background 0.5s;
}
.listtable tr:hover {
  background: #303030 !important;
}
#main th, #main th a {
  color: #757575 !important;
}
/*files*/
.button {
  background: #292929;
}
.user-avatar, .user-chracteristic, .user-stat, .game_newcomment_container, .game_screen, .video_screen, .game-cover-item, .game_tabs, .gamefiles-info {
  background: #202020 !important;
  border: 0px;
}
.gamefiles-left, .bookmark-icon {
  transition: all 0.5s;
	opacity: 0.3;
  border-radius: 5px;
}
.current, .tabs li:hover {
  background: #101010 !important;
}
.game_review_gradient {
  background: none;
}
.game_review_button {
  border-top: 1px solid #202020;
}
.game_review_button a, .game-groups li  {
  background: #202020;
}
.bggray {
  background: #202020;
}
.game-groups li:hover, .bglitegray, .darkgrayline, .game_techinfo_container {
  background: #252525  !important;
  border: none;
}
hr {
  background: #252525  !important;
  border-style: none;
  height: 2px;
}
.bglitegray {
  border: 1px solid #161616 !important;
}
/*headepanel*/
div.headerpanel a, div.headerpanel {
  background: #161616 !important;
  border: 0px;
}
#nav li {
  border-right: 1px solid #292929;
}
#nav ul li, #usermenu ul li {
	border-left: 1px solid #292929 !important;
  border-right: 1px solid #292929 !important;
  border-bottom: 1px solid #292929 !important;
}
.select2-drop, .select2-container td {
  background: #161616 !important;
  border-top: 0px;
  border: 0px;
  color: #757575;
}
.pager ul {
  border: 0px !important;
}
.pager li, .page-current, .tip-list, #utilmenu li  {
  border: 0px !important;
  margin-left: 1px;
  background: #202020 !important;
  transition: opacity 0.5s;
}
#main blockquote {
  background: #252525 !important;
  border-left: 5px solid #303030 !important;
}
.tip-list {
  margin-left: 0px !important;
}
.pager li:hover {
  opacity: 0.5;
}
.ui-widget-content{
  background: #202020;
  box-shadow: 0px 1px 3px 1px #161616;
  color: #757575;
  border: 0px !important;
}
#utilmenu {
  background: #252525;
  border: 0px;
}
.download_info {
  color: #808080;
}
}
