css:
@-mod-document domain('tibia.fandom.com') {


    .page__main > a {
        color: rgb(93, 166, 64)
    }
    :root {
        --theme-body-background-color: #12170f;
        --theme-body-background-color--rgb: 18,25,18;
        --theme-link-color: rgb(93 166 64);
        --theme-link-color--rgb: 0,205,208;
        --theme-link-color--hover: rgb(167 236 140);
        --theme-link-label-color: #000;
        --theme-accent-color: #1f2b30;
        --theme-accent-color--rgb: 31,43,48;
        --theme-accent-color--hover: #47626d;
    }
    .fandom-sticky-header {
        background-color: #284c2b;
    }
    body.theme-fandomdesktop-dark .quest-spoiler > .showhide-header {
        background-color: #281c1e;
        border: 1px solid #680305;
        color: #9d2b2c;
    }
    .loot-table {
        border: 0.1em solid #7990aa;
        border-radius: 1em;
        max-height: 90px;
        overflow-y: scroll;
        overflow-x: hidden;
    }
    body.theme-fandomdesktop-dark .messagebox {
        border: 1px #78402b solid;
        background-color: #1f240d;
    }
    .webdesc {
        max-height: 30px;
    }
    .twbox .webdesc {
        border: none;
        border-radius: none;
        font-style: italic;
        margin: none;
        overflow: auto;
        padding: none;
    }
.page-content h3 {
    margin: none;
}







}
