css:
@-moz-document domain(example.com) {
#body {
  background: rgb(36, 36, 36);
  color: rgba(255, 255, 255, 0.87);
}

#body a,
#body a:visited,
#body a:link {
  color: inherit;
}

#body,
#body div,
#body h1,
#body h2,
#body h3,
#body h4,
#body h5,
#body h6,
#body p,
#body ul,
#body li,
#body dd,
#body dt {
  font-family: sans-serif;
}

div#header #site-title {
	margin-bottom: 8px;
}

div#header ul#navbar {
	padding: 0;
  display: flex;
  gap: 8px;
  padding: 0px 20px;
  margin-bottom: 8px;
}

div#header ul#navbar li {
	font-size: 14px;
	padding: 0;
	margin: 0;
}

div#header ul#navbar li.current-page {
	background: none;
}

div#header ul#navbar li a {
  display: block;
  background: rgb(58, 58, 58);
  padding: 0 12px;
  line-height: 30px;
  border-radius: 8px;
  border: 1px solid rgb(72, 72, 72);
  transition: background 250ms ease;
}

div#header ul#navbar li a:hover {
	background: rgb(72, 72, 72);
}

div#header ul#navbar li.current-page a {
  background: #73de76;
  border-color: transparent;
  transition: background 250ms ease;
  font-weight: 700;
  color: #fff;
}

div#header ul#navbar li.current-page a:hover {
	background: #87d489;
}

div#header ul#subnavbar {
	padding: 0;
  display: flex;
  gap: 14px;
  padding: 0px 20px;
  margin-bottom: 4px;
  background: none;
}

div#header ul#subnavbar li {
	font-size: 14px;
	padding: 0;
	margin: 0;
}

div#header ul#subnavbar li a {
  display: block;
  line-height: 28px;
  transition: color 250ms ease;
}

div#header ul#subnavbar li a:hover {
  color: white;
}

#body h5 {
	font-size: 24px;
  margin-bottom: 4px;
}

#body h6 {
	font-size: 16px;
  margin-top: 8px;
  margin-bottom: 4px;
}

div.sidebar {
	max-width: 250px;
}

div.tag-search {
	width: 250px;
}

div.tag-search input[type="text"] {
	width: 100%;
	padding: 0;
	clear: both;
	box-sizing: border-box;
  background: rgb(26, 26, 26);
  border-radius: 8px;
  border: 1px solid transparent;
  padding: 0 12px;
  line-height: 30px;
  transition: border-color 250ms ease;
  color: inherit;
}

div.tag-search input[type="text"]:hover {
	border-color: rgb(100, 108, 255);
}

div.tag-search input[type="text"]:focus {
  outline: none;
	border-color: rgb(100, 108, 255);
}

div.tag-search input[type="submit"] {
  display: block;
  background: rgb(58, 58, 58);
  padding: 0 12px;
  line-height: 26px;
  border-radius: 8px;
  border: 1px solid rgb(72, 72, 72);
  color: inherit;
  margin-top: 4px;
  transition: background 250ms ease;
}

div.tag-search input[type="submit"]:hover {
	background: rgb(72, 72, 72);
  cursor: pointer;
}

.awesomplete > ul {
	background: rgb(58, 58, 58);
  border-radius: 12px;
  border: 1px solid rgb(72, 72, 72);
}

.awesomplete > ul::before {
	background: rgb(58, 58, 58);
}

.awesomplete li {
	font-size: 14px;
}

.awesomplete > ul > li:hover,
.awesomplete > ul > li[aria-selected="true"] {
	background: rgb(72, 72, 72);
	color: inherit;
}

.awesomplete mark {
  background: rgb(90, 90, 90);
  color: inherit;
  font-weight: 700;
}

ul#tag-sidebar > li.tag-type-copyright,
ul#tag-sidebar > li.tag-type-character,
ul#tag-sidebar > li.tag-type-artist,
ul#tag-sidebar > li.tag-type-general,
ul#tag-sidebar > li.tag-type-metadata {
	display: flex;
  align-items: flex-start;
  gap: 2px;
  margin-bottom: 8px;
  position: relative;
}

ul#tag-sidebar > li > a:nth-child(1) {
	order: 4;
  display: none;
  position: absolute;
  top: 0;
  right: -16px;
  box-sizing: border-box;
  align-items: center;
  justify-content: center;
  height: 100%;
  width: 30px;
}

ul#tag-sidebar > li:hover > a:nth-child(1) {
	display: flex;
}

ul#tag-sidebar > li > a:nth-child(2) {
	order: 1;
}

ul#tag-sidebar > li > a:nth-child(3) {
	order: 2;
}

ul#tag-sidebar > li > a:nth-child(4) {
	order: 3;
}

ul#tag-sidebar > li > span.tag-count {
	display: none;
}

ul#tag-sidebar > li.tag-type-copyright > a,
ul#tag-sidebar > li.tag-type-character > a,
ul#tag-sidebar > li.tag-type-artist > a,
ul#tag-sidebar > li.tag-type-general > a,
ul#tag-sidebar > li.tag-type-metadata > a {
  background: rgb(58, 58, 58);
  padding: 2px 4px;
  border-radius: 6px;
  border: 1px solid rgb(72, 72, 72);
  color: inherit;
  font-size: 14px;
  transition: background 250ms ease;
}

ul#tag-sidebar > li.tag-type-copyright > a:hover,
ul#tag-sidebar > li.tag-type-character > a:hover,
ul#tag-sidebar > li.tag-type-artist > a:hover,
ul#tag-sidebar > li.tag-type-general > a:hover,
ul#tag-sidebar > li.tag-type-metadata > a:hover {
	background: rgb(72, 72, 72);
  color: inherit;
}


.tag-type-copyright,
.tag-type-copyright > a:hover,
.tag-type-copyright:hover {
	color: #D4498F;
}

.tag-type-character,
.tag-type-character > a:hover,
.tag-type-character:hover {
	color: #73DE76;
}

.tag-type-artist,
.tag-type-artist > a:hover,
.tag-type-artist:hover  {
	color: #DE5466;
}

.tag-type-general,
.tag-type-general > a:hover,
.tag-type-general:hover {
	color: inherit;
}

.tag-type-metadata,
.tag-type-metadata > a:hover,
.tag-type-metadata:hover {
	color: #FFA033;
}









}
