css:
@-moz-document domain("fandom.com") {
.top-ads-container,#rail-boxad-wrapper,.ac-player-ph,#WikiaAdInContentPlaceHolder,.gpt-ad,.ad-slot,.mcf-card-article__link,.global-navigation__top > div:nth-child(2), .desktop-article-video-wrapper, #WikiaBar{
    display:none
}
.global-navigation,.global-navigation__bottom,.global-navigation__top, .global-navigation {
    background-color:#111
}
.global-navigation__nav .wds-icon,.global-navigation__bottom .wds-icon,.global-navigation__label,.global-navigation__logo, .global-navigation__link-text, .global-navigation__link-ico, .global-navigation__link-ico svg, .global-navigation__link {
    fill:#eee!important;
    color:#eee!important;
}
.global-navigation__icon.has-background,.global-navigation__search.global-navigation__icon,.global-navigation__icon.has-border,.global-navigation__icon, .global-navigation__link-icon{
    background-color:#222;
    border:none !important;
}
.global-navigation__icon.has-background:hover,.global-navigation__link:hover .global-navigation__icon.has-background,.global-navigation__search.global-navigation__icon:hover,.global-navigation__link:hover .global-navigation__icon.has-border,.wds-dropdown__toggle:hover .global-navigation__icon, .global-navigation__link:hover .global-navigation__link-icon{
    background-color:#333;
    border: none;
}

.feed-header {
 background: #111;   
}

.search-box__container, .top-wikis-block {
background: #222;
}

.feed-section {
	border-color: #111;
}

.feed-section__heading h2 {
border-color: #000;
    background: #111;
}

}
