@-moz-document domain("fandom.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.global-navigation__link-text
{
	text-transform: capitalize!important;
}

.global-navigation__link-icon.start-a-wiki,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.global-navigation__link-text
{
	font-size: 1.25rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.article-layout__rail,
.feed-section__ad,
.has-uap .top-ads-container,
.has-uap .top-ads-container .ad-slot-placeholder.top-leaderboard #top_leaderboard,
.has-uap .top-ads-container .ad-slot-placeholder.top-leaderboard
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.global-navigation__link-text,
.global-navigation__logo .global-navigation__link-text
{
	max-width: none!important;
}

.article-layout-wrapper
{
	width: 78%!important;
}

.global-navigation {
    width: 7%!important;
}

}

