@-moz-document domain("fandom.com"), domain("auth.fandom.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

.article-image__caption,
.feed-card__summary,
.global-navigation,

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.wds-button.wds-is-secondary,
.feed-card__title,
.feed-popular-wikis .popular-wikis-wrapper .popular-wiki-container-title,
.sc-hGPBjI.fgaeMo,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

.feed-popular-wikis .popular-wikis-title,
.feed-section__news-stories-topic,
.global-navigation__link-text
{
    color: #ff3cde;
	text-shadow: 1px 1px #fff;
}


}

@-moz-document domain("fandom.com"), domain("auth.fandom.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.wds-global-footer.is-f2 {
    background: linear-gradient(0deg, rgba(0, 0, 0, 0.5), rgb(0 0 0)), #33012a!important;
}

.wds-dropdown__content:not(.wds-is-not-scrollable) .wds-list
{
	background-image: none!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.Layout_main__2Cy2L,
.article-header__title>h1,
.article-image__caption,
.feed-card__title-summary,
.feed-layout,
.global-navigation__bottom,
.global-navigation__link-icon,
.wds-dropdown__content .wds-list:after,
.wds-dropdown__content .wds-list:before
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.global-navigation__bottom .wds-button
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.global-navigation__bottom .wds-button::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.global-navigation__bottom .wds-button:hover::before
 {
  opacity: 1!important;
}

/* Light Blue 
_____________________________________________________________________________________________________*/

.wds-button
{	
	background: radial-gradient(circle, #3f4040a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.wds-button::before
{
	background: radial-gradient(circle, #000 0%, #3f4040 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.wds-button:hover::before {
  opacity: 1!important;
}

/* Purple 
_____________________________________________________________________________________________________*/

.wds-button:not(:disabled)
{	
	background: radial-gradient(circle, #eb00c3a8 0%, #000 100%);
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.wds-button:not(:disabled)::before
{
	background: radial-gradient(circle, #000 0%, #eb00c3 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.wds-button:not(:disabled):hover::before {
  opacity: 1!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.AuthLayout_layout__opByk,
.global-navigation__link.wds-dropdown.wds-open-to-right .wds-dropdown__content,
.sc-fKVqWL.gtOrYx,
.wds-dropdown__content
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.AuthLayout_layout__opByk:hover,
.global-navigation__link.wds-dropdown.wds-open-to-right .wds-dropdown__content:hover,
.sc-fKVqWL.gtOrYx:hover,
.wds-dropdown__content:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Dark Gray Gradient 
_____________________________________________________________________________________________________*/

.global-navigation
{
	background: linear-gradient(to left, #202020 0%, #000 100%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.wds-dropdown__content .wds-list a:hover:before
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}


}

@-moz-document domain("fandom.com"), domain("auth.fandom.com") {
/* Image Settings */

.a-list-feed__item-container,
.feed-card,
.feed-popular-wikis .popular-wikis-wrapper__link,
.feed-popular-wikis .popular-wikis-wrapper__link.with-ad,
.global-navigation__link,

p
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.a-list-feed__item-container:hover,
.feed-card:hover,
.feed-popular-wikis .popular-wikis-wrapper__link.with-ad:hover,
.feed-popular-wikis .popular-wikis-wrapper__link:hover,
.global-navigation__link:hover,

p:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

