/* ==UserStyle==
@name         Wykop - czytelne wpisy na Mikroblogu 
@version      1.0.4
@namespace    userstyles.world/user/wykopchanger
@description  Czytelniejsze wpisy na Mikroblogu i pod tagami
@author       wykopchanger
@license      No License
==/UserStyle== */
@-moz-document domain("wykop.pl") {
    section.tag-page section.stream section.tags-stream > div.content > section,
    section.microblog-page section.stream.microblog > div.content > section.entry {
        margin-bottom: 70px;
        padding-bottom: 25px;
        padding-top: 25px;
        border-radius: 0px;

        box-shadow: 2px 6px 5px rgb(0 0 0 / 40%);

        border-bottom: 1px solid red;
        border-top: 1px solid rgb(67, 131, 175);
    }
}
