css:
@-moz-document domain("powerschool.com") {

body, #content-main{
    background: #000711;
    /* Can't decide if I want this font to be included
    font-family: Inter, serif;
    */
}

#usercontext-bar, .code {
    display: none;
}

#tools {
    transform: translate(-15px, 50%);
}

#nav-main {
    box-shadow: 0 0 1px 0;
}

#nav-main li {
    border-radius: 5px;
}

.psparent #nav-main a {
    color: #ffe7e7;
}
#nav-secondary, .sessiontimeoutwarningBox, .feedback-note, .feedback-info {
    margin: 5px;
    background-color: #020d1e;
}
#nav-secondary li,
.tabs a {
    background: #263c57;
    border: none;
    border-radius: .5em;
    color: white;
}

table.grid th, .box-round {
    border: none;
    
}

.selected {
    border-radius: 5px;
    background-color: #2f4f6c !important;
}

h1,
h2 {
    font-family: 'Zilla Slab', serif;
    letter-spacing: .05em;
    color: #f0f0f0;
}

.linkDescList, linkDescList td, .grid, .grid td{
        border: solid 1px #564373 !important;
}

td {
    font-size: 1.1em;
    color: #cecece !important;
    background: #000711 !important;
}

table.grid tr:nth-child(2n) > td {
    background-color: #1c2d42 !important;
}

th,
#schoolinfoview div {
    background: rgba(6, 21, 50, 1);
    color: #ececec;
    border: none;
}

#attByClass,
.scheduleHeader {
    background: rgba(6, 21, 50, 1) !important;
    border-radius: .5em .5em 0 0;
    color: #ececec;
}

a {
    font-family: 'Inter', serif;
    font-size: .9em;
    color: #5393ce;
}

span {
    font-family: 'Inter', serif;
    color: #e1e1e1;
}

#header, .ng-binding th {
    background: rgba(6, 21, 50, 1) !important;
    color: #e8e8e8;
}

.grid, .ui-widget-overlay, .notInSession {
        background: repeating-linear-gradient(
  -60deg,
  #000711,
  #000711 5px,
  #000831 5px,
  #000831 10px
) !important;
}

}
