css:
section.hits-slider-stream {

    flex-direction: row-reverse;

    display: flex;

}

.hits-slider-stream header.stream-top {

    flex-direction:column;

    margin-left: 1rem;

    margin-right: 1rem;

    flex-flow: column-reverse;

    transform: rotate(90deg);

    display: flex;

    width: 2rem;

}

.hits-slider-stream header.stream-top h1 {

    display:none

}

section.stream.hits-slider-stream.from-pagination-hits-slider-stream div.content {

    width:90%;

    flex-grow: 1;

}

.hits-slider.wide-top content {

    display: flex;

    flex-direction: row-reverse;

}

aside.hits-slider div.swiper-slide {

    width:10rem !important;

    height:8rem !important

}

div.main-content main.main section section.sidebar {

    display: flex;

    flex-direction: column-reverse;

    justify-content: flex-end;

}

/*mikroblog*/

html.chrome.desktop.landscape.firefox body.ihmn section.default-layout.home-context div.main-content main.main section section.sidebar section.custom-sidebar:nth-child(3) {

    order: 10

}

/* wykopalisko*/

html.chrome.desktop.landscape.firefox body.ihmn section.default-layout.home-context div.main-content main.main section section.sidebar section.custom-sidebar:nth-child(4) {

    display:none

}

/* hity tygodnia*/

html.chrome.desktop.landscape.firefox body.ihmn section.default-layout.home-context div.main-content main.main section section.sidebar section.custom-sidebar:nth-child(2) {

    order: 5

}

main.main {

    display: flex;

    flex-direction: column-reverse;

}
body.ihmn section.default-layout.home-context header.header
{
    background: #070D12 !important
}
