css:
@-moz-document domain("wykop.pl") {
/*      WYKOPKI      */
/*        CML        */
/*       1.1.0       */
/*   CRT: 17.01.23   */
/*   REL: 22.01.23   */
/*   UPD: 26.01.23   */


/* hide */
.pub-slot-wrapper, section.no-placeholder { display: none !important; }
main.main[data-theme="default"] > aside.wide-top { display: none; }
.stream-top { display: none !important; }
.top-category-wallpaper > figure img { display: none !important; }
header strong.upcoming-count { display: none !important; }
.popper-button button.target { display: none !important; }
section.register { display: none !important; }


/* color */
[data-night-mode] section.rating-box > ul li.plus { color: var(--apple) !important; }
[data-night-mode] section.rating-box > ul li:not(.zero):not(.plus) { color: var(--orange) !important; }
:root { scrollbar-color: #264d67 #0c1720 !important; }
::-webkit-scrollbar-track { background-color: #0c1720 !important; }
::-webkit-scrollbar-thumb { background-color: #264d67 !important; }
::-webkit-scrollbar-thumb:hover { background-color: #4383af !important; }


/* misc */
header.stream-top { padding: 2px 0px !important; }
.popper-button button.target { width: 32px !important; height: 32px !important; }
.popper-button button.target::after { top: 5px !important; left: 5px !important; width: 18px !important; height: 18px !important; }
body > section.open-left-panel:not(.is-mobile) main.main { max-width: calc(100% - 200px); }
body > section:not(.is-mobile) main.main { max-width: calc(100% - 48px); }


/* left */
aside.left-panel.mini { width: 48px !important; opacity: 0.75 !important; margin-right: 152px !important; }
aside.left-panel { 
    width: 200px !important; 
    margin-left: 0 !important; 
    top: calc(48px - 12px) !important; 
    height: calc((var(--vh, 1vh) * 100) - 42px) !important; 
}
aside.left-panel > footer { width: 200px !important; }
aside.left-panel section.cta-bucket > footer p { display: none !important; }
aside.left-panel section.cta-bucket > header h4 { font-size: 12px !important; opacity: 0.7 !important; }
aside.left-panel section.cta-bucket > header + .content { margin-top: 5px !important; }
aside.left-panel > section.links > .content ul li a { 
    font-size: 14px !important; line-height: 26px !important; height: 26px !important; 
}
section.add-to-bucket-btn > button { 
    font-size: 12px !important; height: 25px !important; padding: 0 10px !important; opacity: 0.8 !important; 
}
section.add-to-bucket-btn.icon > button { padding-left: 36px !important; }


/* right */
section.custom-sidebar { padding: 10px 20px !important; }
section.custom-sidebar section.link { padding: 8px 20px !important; }
section.custom-sidebar section.entry-content { font-size: 13px !important; line-height: 16px !important; opacity: 0.8 !important; }
section.custom-sidebar section.entry-content .wrapper button { margin-top: 5px !important; }
section.custom-sidebar section.entry { padding: 10px 20px !important; }
section.custom-sidebar section.entry > header + .content, 
section.custom-sidebar section.entry > .content + footer { margin-top: 5px !important; }


/* content */
section.register { padding: 8px 15px 8px 15px !important; }
section.register article header h2 { font-size: 16px !important; line-height: 16px !important; }
section.register article header + .content { margin-top: 2px !important; }
section.register article > .content p { opacity: 0.8 !important; }
.stream section.register div.button .target { height: 25px !important; padding: 0 15px !important; }
section.adult-placeholder { padding: 12px !important; }
section.adult-placeholder > header h3 { font-size: 15px !important; line-height: 15px !important; }
section.adult-placeholder.link-stream > header span.adult-ribbon { 
    font-size: 12px !important; line-height: 18px !important; height: 20px !important; 
}
section.adult-placeholder.link-stream > * { margin-top: 5px !important; }
.stream section.adult-placeholder div.button .target { 
    font-size: 12px !important; height: 25px !important; padding: 0 10px !important; 
}
section.vote-box { margin: 15px 0 !important; }
section.link-block, .stream section.adult-placeholder, .stream section.register { margin-top: 8px !important; }
section.stream > header.stream-top.background + * { margin-top: 8px !important; }
section.link-block > section > article figure { 
    top: 15px !important; right: 15px !important; height: 100px !important; max-width: 150px !important; 
}
section.link-block > section > aside { width: 75px !important; }
section.link-block > section > article { width: calc(100% - 75px) !important; padding: 15px 200px 15px 0 !important; }
section.link-block > section > article > header h2 { font-size: 15px !important; line-height: 18px !important; }
section.link-block > section > article > .content { margin: 4px 0 !important; }
section.link-block > section { min-height: 130px !important; }
section.link-block > section > article section.info > p { opacity: 0.8 !important; font-size: 13px !important; }
section.link-block > section > article section.info > span { margin-top: 4px !important; line-height: 16px !important; }
section.actions > ul > li.tag { margin: 0px 6px 0px 0 !important; }
section.entry-content { font-size: 13px !important; line-height: 16px !important; }
section.entry-content .wrapper button { padding: 1px 6px 2px 6px !important; }
section.entry-content .wrapper button.more { margin-left: 5px !important; }
section.entry-photo figure { padding: 4px 4px 22px 4px !important; }
section.entry-photo figure figcaption { height: 20px !important; line-height: 20px !important; }
aside.top-category { padding: 10px 0 0px 0 !important; }
aside.top-category > article > figure { 
    height: 35px !important; width: 35px !important; -ms-flex: 0 0 35px !important; flex: 0 0 35px !important; 
}
aside.top-category > article > header h1 { font-size: 16px !important; }
aside.top-category > article > header h1 + p { margin-top: 0px !important; }
aside.top-category > article > header p { font-size: 12px !important; }
aside.top-category > article { min-height: 40px !important; }
span.comment-counter::before, a.comment-counter::before { 
    width: 15px !important; 
    height: 16px !important; 
    -webkit-mask-size: 15px 16px !important; 
    mask-size: 15px 16px !important; 
    top: 0px !important; 
}


/* hits */
aside.hits-slider .swiper-container .swiper-wrapper .swiper-slide { 
    width: 110px !important; 
    height: 70px !important; 
    margin-right: 5px !important; 
}
aside.hits-slider section.vote-box.hot .dig p::before, section.vote-box.hot .dig p::after { display: none !important; }
aside.hits-slider .swiper-container .swiper-wrapper .swiper-slide a .vote-box { left: 4px !important; top: -2px !important; }
aside.hits-slider section.vote-box .dig p { padding: 2px !important; border-radius: 5px !important; }
aside.hits-slider section.vote-box .dig p span { 
    border-radius: 5px !important; 
    min-width: 25px !important; 
    max-width: 40px !important; 
    width: 30px !important; 
    height: 11px !important; 
    line-height: 11px !important; 
    font-size: 10px !important; 
}
aside.hits-slider .swiper-container .swiper-wrapper .swiper-slide a figure figcaption {
    left: 4px !important;
    right: 4px !important;
    bottom: 4px !important;
    font-size: 12px !important;
    line-height: 15px !important;
    text-shadow: 1px 1px 1px #000 !important; 
}
aside.hits-slider .swiper-container > button { width: 20px !important; height: 25px !important; }


/* menubar */
@media screen and (min-width: 1151px) {
header strong.upcoming-count {
    opacity: 1 !important;
    border-radius: 4px !important;
    font-size: 10px !important;
    padding: 0px 1px 0 1px !important;
    line-height: 11px !important;
    top: 0px !important;
    right: 0px !important;
}
header strong.upcoming-count::before {
    border-radius: 4px !important;
    font-size: 10px !important;
    padding: 1px 2px 0 2px !important;
    line-height: 11px  !important;
    display: none !important; 
}
header.header { height: 36px !important; }
header.header > .left > button { width: 36px !important; height: calc(100% - 10px) !important; }
header.header > .left > a.logo { width: 25px !important; mask-size: 25px 25px !important; }
header.header > .left > a.logotype { width: 115px !important; mask-size: 85px 20px !important; }
header aside.doodle > * img { height: 35px !important; }
header aside.doodle { top: 0px !important; }
header.header > .right > section.search-input { top: 4px !important; }
header section.search-input form { height: 77% !important; }
header section.search-results-dropdown { top: calc(100% + 2px) !important; }
header.header > .left > nav.main { height: 38px !important; }
header.header > .right > nav > ul > li > a { 
    height: calc(48px - 22px) !important; 
    line-height: calc(48px - 22px) !important; 
    top: 5px !important; 
}
}

}
