css:
@-moz-document domain("wikipedia.org") {
html {
  filter: invert(100%);
}

img, image {
  filter: invert(80%);
}

.mw-logo-container, .legend-color {
  filter: invert(1);
}

.mw-mmv-image-wrapper {
  background: #fff;
}

a,
.tocnumber {
  color: #bf9a00 !important;
  text-decoration: none;
}

a:hover {
  color: #bcaa6c !important;
  text-decoration: underline;
}

a.new {
  color: #66cccc !important;
}
}
