/* i really want this to be global */
:root {--secondary: #000000; --secondary-rgb: #000000; --header_background: rgba(0, 0 , 0, 0.7); --primary-low: rgba(255, 255 , 255, 0.05); --primary-very-low: rgba(255, 255 , 255, 0.02); --primary: #FFFFFF; --blend-primary-secondary-5: rgba(255, 255 , 255, 0.05);}
.contents.clearfix {display: none}
blockquote {border: none}
aside.quote .title {border: none}
.presence-users {background-color: #000000}
img.avatar {border-radius: 25%}
.user-card-avatar {border-radius: 25%}
.quote aside .quote, .quote aside .title, .quote aside blockquote, .quote aside .onebox, .quote aside .onebox-result {
 border: none
}
