@-moz-document domain("gx.games") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.bkreRo,
.dFCOOB.active::after,
.fTObrv,
.fFBqQq,
.fDJsz,
.jKNhte,

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

.eUTFLQ:not([disabled]):focus-visible, .eUTFLQ:not([disabled]):hover,
.fWsEWL:not([disabled]):active,
.fWsEWL:not([disabled]):focus-visible,.fWsEWL:not([disabled]):hover,
.gcNUMF:not([disabled]):focus-visible, .gcNUMF:not([disabled]):hover,
.gqZBXt:not([disabled]):focus-visible,.gqZBXt:not([disabled]):hover,
.hHGXzk:hover,
.iLebtm:not([disabled]):focus-visible,.iLebtm:not([disabled]):hover,
.iTaOPK:not([disabled]):focus-visible, .iTaOPK:not([disabled]):hover,
.iWAFMD:not([disabled]):focus-visible,.iWAFMD:not([disabled]):hover,
.kYbWa-d:not([disabled]):focus-visible, .kYbWa-d:not([disabled]):hover,
.kksxai:not([disabled]):focus-visible, .kksxai:not([disabled]):hover
{
    transform: none!important;
}

.cQeByr {
    white-space: pre-line;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.fUoPky
{
	font-size: 2.5rem!important;
}

.jJEmyJ
{
	font-size: 1.52rem!important;
}

.jJEmyJ
{
	line-height: 2.1rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

selector
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.hphqDc,
.jKNhte
{
	padding: 13px 26px!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

.BIVtD,
.bYOFAe,
.bdPjnS,
.jwiAyV
{
	max-width: none!important;
}

}

@-moz-document domain("gx.games") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

.eAMRNV,
.fDJsz,
.hphqDc:hover,
.jKNhte:hover,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.fUoPky,
.hhCLAP,
.iLjSdw.iLjSdw.header > div,

h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

.fUxGip,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #1c1726!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

selector
{
	color: #fff!important;
}


}

@-moz-document domain("gx.games") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.lhXONF.completed .sc-ce4e679f-2 {
    box-shadow: #fa1e4e6b 8px 12px 19px;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.fTObrv,
.hphqDc,
.jKNhte
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.kksxai
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.kksxai::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.kksxai:hover::before
 {
  opacity: 1!important;
}

/* Purple 
_____________________________________________________________________________________________________*/


.kYbWa-d,
.ZPhOn
{	
	background: radial-gradient(circle, #7b61ffa8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	padding: 3px 26px;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.kYbWa-d::before,
.ZPhOn::before
{
	background: radial-gradient(circle, #000 0%, #7b61ff 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.kYbWa-d:hover::before,
.ZPhOn:hover::before
{
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.iTaOPK,
.iLebtm,
.iWAFMD
{	
	background: radial-gradient(circle, #fa1e4ea8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.iTaOPK::before,
.iLebtm::before,
.iWAFMD::before
{
	background: radial-gradient(circle, #000 0%, #fa1e4e 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.iTaOPK:hover::before,
.iLebtm:hover::before,
.iWAFMD:hover::before
{
  opacity: 1!important;
}

/* Light Blue 
_____________________________________________________________________________________________________*/

.gqZBXt
{	
	background: radial-gradient(circle, #058db7a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.gqZBXt::before
{
	background: radial-gradient(circle, #000 0%, #058db7 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.gqZBXt:hover::before {
  opacity: 1!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.lfGvPS,
.vhzgu,
.xRhqE
{
	background-color: #1c1726!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

.lfGvPS:hover,
.vhzgu:hover,
.xRhqE:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Purple Gradient 
_____________________________________________________________________________________________________*/

.llySjo
{
	background: linear-gradient(to bottom, #7b61ff 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
}

.llySjo:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.cJMIIT
{
	background: linear-gradient(to bottom, #1c1726 0%, #000 100%)!important;
}

.gfqdjZ
{
	background: linear-gradient(to top, #1c1726 0%, #000 80%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.iLjSdw:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.bkreRo,
.cUmsoa,
.fDJsz,
.hphqDc:hover,
.jKNhte:hover
{
	background: radial-gradient(ellipse at center, #5c4e7e 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

.fFBqQq,
.fWsEWL:not([disabled]):active
{
	background: radial-gradient(ellipse at center, #7b61ff 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

.hwCBzd
{
	background: radial-gradient(ellipse at center, #fa1e4e 0%,#000 70%)!important;
	padding: 13px 26px!important;
}


}

@-moz-document domain("gx.games") {
/* Image Settings */

.caJmMz,
.cVlCTm,
.dSXrLL,
.hHGXzk,
.iLjSdw,
.jXtOae,
.jzVgLV
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

.caJmMz:hover,
.cVlCTm:hover,
.dSXrLL:hover,
.hHGXzk:hover,
.iLjSdw:hover,
.jXtOae:hover,
.jzVgLV:hover
 {
	filter: brightness(100%)!important;
}

/*
_____________________________________________________________________________________________________*/

.ifHyUW,

img
 {
	border-radius: 22px!important;
}

/*
_____________________________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*
_____________________________________________________________________________________________________*/

.cQeByr,
.kaNUTl
 {
	opacity: 1!important;
}

}

