css:
/* i really want this to be global */

#header
{
  background-color: #7cc0f4;
}

.ad,
    .ad *,
    .img_ad,
    .ad-annotations,
    #google_image_div,
    #aw0,
    #AdvertisingLeaderboard
{
  display: none !important;
}
    /*Ad removal */
    /* Start http://www.cursors-4u.com */
    
body,
    a:hover
{
  cursor: url(http://cur.cursors-4u.net/nature/nat-10/nat996.cur), progress !important;
}
    /* End http://www.cursors-4u.com */

.navigation .notification-icon,
.rbx-highlight
{
  background: #7cc0f4;
}

#chat-header
{
  background: #7cc0f4;
}

.slide-item-container-left,
.profile-avatar-right
{
  background: #7cc0f4;
}

.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active
{
  box-shadow: 0 -4px 0 0 #ffaeff inset;
}

.forum-table-header,
body .forum-table-header th,
body .forum-table-footer,
#ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
#ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
{
  background: #ffaeff !important;
}

.rbx-upgrade-now #upgrade-now-button,
.rbx-btn-secondary-xs.btn-more,
#AjaxCommentsContainer > div > div.comment-form > form > button,
.rbx-vip-server-create,
.btn-secondary-xs
{
  background-color: #7cc0f4 !important;
  border-color: #7cc0f4;
}

.rbx-upgrade-now #upgrade-now-button:hover,
.rbx-btn-secondary-xs.btn-more:hover,
#AjaxCommentsContainer > div > div.comment-form > form > button:hover,
.rbx-vip-server-create:hover,
.btn-secondary-xs:hover
{
  background: #7cc0f4 !important;
  border-color: #7cc0f4;
}

#TopAbpContainer,
.ad-annotations,
#LeftGutterAdContainer,
#RightGutterAdContainer,
#Skyscraper-Adp-Right,
#Skyscraper-Adp-Left,
.abp-spacer,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
#GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
.sponsored-game,
.alert-info,
#mCSB_1_container > ul > li.font-bold.small,
#mCSB_1_container > ul > li.rbx-nav-sponsor
{
  display: none !important;
}

#navbar-search-input,
#navbar-search-btn
{
  border: 1px solid #ffaeff;
}

#MasterContainer,
.nav-content-inner,
.container-main,
.gutter-ads,
#Body,
#wrap > div.container-main > div
{
  background: url(https://www.bing.com/images/search?view=detailV2&ccid=3bdUF1hY&id=7F84FD919138E05F4B2AA47C20BB815B5888EF04&thid=OIP.3bdUF1hY6-W9_-nk_uY-fwHaEL&mediaurl=https%3a%2f%2fak.picdn.net%2fshutterstock%2fvideos%2f1009388666%2fthumb%2f1.jpg&cdnurl=https%3a%2f%2fth.bing.com%2fth%2fid%2fR.ddb754175858ebe5bdffe9e4fee63e7f%3frik%3dBO%252bIWFuBuyB8pA%26pid%3dImgRaw%26r%3d0&exph=480&expw=852&q=White+Background+Blue+Stars&simid=608035806260629930&FORM=IRPRST&ck=8AD58AE8EAEC7C05B01CD313F29D67CB&selectedIndex=11&ajaxhist=0&ajaxserp=0) top center repeat-x #e3e3e3;
  background-size: cover !important;
  background-attachment: fixed !important;
}

.profile-avatar-left,
#tab-wardrobe,
#characterInventorySearch,
#tab-outfits,
#HomeContainer > div.col-xs-12.home-header > a
{
  background: transparent !important;
}

#party_none_title,
div.friend_dock_chatbox_titlebar.chat-header-blink-off
{
  background: linear-gradient(to bottom, #ffaeff, #ffaeff);
}

#ctl00_cphRoblox_RightColumn,
#Body > table > tbody > tr > td:nth-child(3),
#Body > table > tbody > tr > td:nth-child(4)
{
  display: none;
}

#GamesPageLeftColumn
{
  width: auto;
  margin-right: 10px !important;
  margin-left: 10px !important;
}

#GamesListsContainer > div
{
  background-color: transparent !important;
  width: 100% !important;
}
