    }
    
    /*Search Bar*/
    .kGWMSU {
    color: #c3c3c3;
    background: #323232;
    }
    
    /*Main Background*/
    .hPmLNC {
    background-color: #323232;
    }
    .euJYQz::before {
    background-color: #323232;
    }
    .Content__OuterWrapper-sc-1f451g1-2.gUBVaK {
    background: #323232;
    }
    .gjWAAG {
    background-color: #323232;
    }
    
    
    /*Title Bar Background*/
    .cHhLGz {
    background-color: #323232;
    }
    .jltJDp {
    background-color: #323232;
    }
    
    /*Table of contents background*/
    .jwGPmf {
    background: #323232;
