    /*Left Nav Bar Background*/
    .euJYQz {
    background-color: #323232;
    }
    
    .dWyFGC > ol::before {
    background: #323232;
    }
    
    .dWyFGC > * {
    background: #323232;
    }
    
    .jwGPmf > ol::before {
    background: #323232;
