css:
/* i really want this to be global */

.image-element  .konvajs-content {
  display: flex;  
}

.image-element  .konvajs-content canvas {
  flex: 1;
}

.wrapper--9LkYv {
  position: absolute !important;
  left: 0;
  top: 0;
  --size: 280px;
  width: var(--size) !important;
  height: var(--size) !important;
  --left: 300px;
}

.wrapper--9LkYv:nth-of-type(2n) {
  left: var(--left) !important;
}

.lsf-main-view__annotation div *:nth-last-child(6) {
  left: calc(var(--left) * 2) !important;
}

.lsf-main-view__annotation div *:nth-last-child(4) {
  left: calc(var(--left) * 0) !important;
  top: calc(var(--left) * 1) !important;
}

.lsf-main-view__annotation div *:nth-last-child(2)  {
  left: calc(var(--left) * 1) !important;
  top: calc(var(--left) * 1) !important;
}

.container--6qUxp {
  position: relative;
  top: 136px;
}

.lsf-main-view__annotation > div {
  padding-top:  40px;
}

.lsf-main-view__infobar,
.lsf-object.lsf-richtext:nth-of-type(1),
.lsf-object.lsf-richtext:nth-of-type(2),
.lsf-object.lsf-richtext:nth-of-type(3),
.lsf-panel,
.ant-typography,
.lsf-toolbar {
  display: none;
}

.lsf-choices {
  position: absolute;
  top: -10px;
  border-radius: 5px;
   background: white;
}

.lsf-sidepanels_collapsed {
  height: 100% !important;
}
