css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.org.ru") {

body { width: 80%; margin: .2cm auto !important; }
#page { margin: auto !important; width: auto !important; text-align: center !important; }
.header { display: none}
.menu { margin: 0 !important; background-color: #fff; color: #000; padding: 5px; font-family: Arial, sans-serif; text-align: left !important; }
div.menu a:link, div.menu a:hover, div.menu a:visited {color: #555}
div#quotes + div.menu { border-bottom: 0 !important; background-color: #fff; text-align: left !important;  margin-bottom: 35px !important; }
table { width: auto !important; }
a, a:visited { color: #0d3ef6 !important; }
div#quotes div.q { font-family: 'Courier New', monospace !important; font-size: 80% !important; }
div#quotes div.q div, div.stats { background: white !important; padding: 0 !important; border: none !important; }
div#quotes div.q div.vote { font-family: inherit !important; }
div.q div.vote a:first-child { font-weight: bold; }
div.q div.vote a:first-child:before { content: '#'; }
div.news { font-family: 'Arial', 'Tahoma', sans-serif !important; }
.b b { font-weight:  normal; }
small { font-family: 'Times New Roman', serif; }
div.header img, div#bar, hr { display: none; }
div.q {border: 2px #e6e6e6 dotted !important; margin-bottom: 25px !important; padding: 10px !important}
div.vote {display: none}
input {width: auto !important}

}
