css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {

.fora tr{border-bottom:8px solid #DEE3DC!important;}

.fora td.v{background-color:#D4DED5!important;border-top:1px solid #DEE3DC!important;padding-bottom:5px!important;color:#4e6257!important;}

.fora td.v a.a{padding-left:20px!important;text-decoration:none!important;}
.fora td.v a.a:hover{text-decoration:underline!important;}
.fora td.v a.a:before{}


#chat{background:#DDE4DC none!important;}

.head{background:#E9EEE8 none!important;border-top:1px solid #C9D4CB!important;border-bottom:1px solid #C9D4CB!important;padding:5px!important;}

.head strong{font-size:18px!important;padding-left:30px!important;font-style:normal!important;padding-bottom:3px!important;color:#798583!important;text-shadow:1px 1px 0px #fff!important;}

form.f div{background:#E9EEE8 none!important;}

h2.chatside{background: #C9D5C7 none!important;border-top:1px solid #9AAFA0!important;border-bottom:1px solid #9AAFA0!important;padding-left:10px!important;color:#6B786F!important;text-shadow:1px 1px 0 #fff!important;}

.shouts dt, .shouts dd{background:#D4DED5 none!important;}

.shouts dt{-moz-border-radius-topleft:3px!important;-moz-border-radius-topright:3px!important;border-width:1px 1px 0px 1px!important; border-color:#9AAFA0!important; border-style:solid!important;}

.shouts dd{-moz-border-radius-bottomleft:3px!important;-moz-border-radius-bottomright:3px!important;border-width:0px 1px 1px 1px!important; border-color:#9AAFA0!important; border-style:solid!important;}

.catbar{color: #498091!important;background: transparent url(http://st.deviantart.net/minish/main/bg-bubblb2.gif) repeat-x top left!important;}

.catbar h1{color:#4e6257!important;text-shadow:1px 1px 0px #e6ede4!important;}

th.first strong{padding-left:20px!important;}

th *, th{background: #E9EEE8 none!important; border-width:0!important;margin-bottom:0!important;padding-bottom:0!important;border-collapse:collapse!important;}
}
