css:
@-moz-document domain(roblox.com)
{
  .L3eUgb
  {
    background: url(https://i.imgur.com/EjonwkR.jpg) center center !important;
    background-size: cover !important;
    background-position-y: -55px !important;
  }

  .o3j99
  {
    background: transparent !important;
  }


    /* change color of bottom texts "About Adertising Bussiness ..." */
  a.pHiOh
  {
    color: gainsboro !important;
  }

    /*location text "United States" */
  .uU7dJb
  {
    color: gainsboro !important/* text color */;
    border-bottom: 1px solid #cccbcb5e;
  }

  a.EzVRq
  {
    color: #fff !important;
  }

  a.gb_g
  {
    color: gainsboro;
  }

  div#SIvCob
  {
    display: none!important;
  }

  .o3j99.ikrT4e.om7nvf
  {
    opacity: 0.75!important;
  }

  .gb_pa
  {
    background-color: rgba(32, 33, 36, 0) !important;
  }

  .b0KoTc
  {
    display: none !important;
  }

  .gb_ua.gb_3d
  {
    min-width: initial;
    min-width: auto;
    background-color: transparent;
  }

  .gb_Ca.gb_2d
  {
    min-width: initial;
    min-width: auto;
    background: #0707075e !important;
    backdrop-filter: blur(10px);
    border-radius: 20px;
  }

  .c93Gbe
  {
    background: #0707075e !important;
    backdrop-filter: blur(10px);
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    max-height: 10px;
    content-visibility: auto;
    transition: 1s;
  }

  .o3j99.c93Gbe:hover
  {
    max-height: 500px;
    transition: 2s;
  }

  .gb_5a:not(.gb_Ld)
  {
    background-color: #000000a3 !important;
    backdrop-filter: blur(5px);
  }

  .LX3sZb
  {
    display: inline-block;
    flex-grow: 1;
    max-width: 100px;
    transition: 1s;
    border-radius: 20px;
  }

  .LX3sZb:hover
  {
    max-width: 300px;
    transition: 2s;
  }

  .gb_Da.gb_4d
  {
    min-width: initial;
    border-radius: 20px;
    min-width: auto;
    backdrop-filter: blur(5px);
    background-color: #0000007d !important;
  }

  .rSk4se
  {
    max-height: 209px;
    position: relative;
  }
}
