@-moz-document domain("accuweather.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.minute-cast-chart #minutecast.base-chart-container .y-labels,
.footer-content-category .footer-content-category-header,
.module-header,
.module-title,
.more-cta-links .cta-link, .more-cta-title,
.thumbnail-right-rail .right-rail-article__category
{
	text-transform: capitalize!important;
}

.featured-search-bar,
:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

selector
{
	font-size: 2.5rem!important;
}

.mapping-layer__content .title-wrapper .title
{
	font-size: 1.85rem!important;
}

.inline-tooltip .inline-tooltip-text,
.air-quality-content .content-wrapper .air-quality-data .statement,
.air-quality-pollutant .statement-wrapper .statement,
.header-search-bar .search-form .search-input,
.mapping-layer__content .description
{
	font-size: 1.52rem!important;
}

selector
{
	line-height: 2.5rem!important;
}

.mapping-layer__content .title-wrapper .title
{
	line-height: 2.1rem!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.glacier-ad
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.header-search-bar .searchbar-inner .search-options .dropdown-legacy .displayed-item .icon-chevron {
    right: 8px;
    top: -1px;
}

.two-column-page-content .page-column-1 {
    margin-left: 2rem;
}

.featured-search-bar .searchbar-content .search-options .dropdown-legacy .displayed-item .icon-chevron {
    right: 7px;
    top: 8px;
}

/* Margins 
_____________________________________________________________________________________________________*/

.monthly-calendar .monthly-daypanel
{
	margin: .5rem!important;
}

selector
{
	margin: 3px 6px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

selector
{
	padding: 1.2rem!important;
}

selector
{
	padding: 13px 26px!important;
}


/* Heights 
_____________________________________________________________________________________________________*/

selector
{
	max-height: none!important;
}

.map-container-legacy .map-canvas {
    height: 35rem;
}

.minutes-group .minute .icon {
    height: 37px;
}

/* Widths 
_____________________________________________________________________________________________________*/

.air-quality-pollutant .statement-wrapper .statement,
.two-column-page-content
{
	max-width: none!important;
}

selector
{
	width: auto!important;
}

.two-column-page-content .page-column-1 {
    width: calc(87% - 328px);
}

.two-column-page-content .page-column-2 {
    width: 22%;
}

selector
{
	width: 98%!important;
}

selector
{
	width: 45%!important;
}

}

