.dropdown-legacy .items .item.active,
.half-day-card .half-day-card-content .panel-item:hover span,
.half-day-card .half-day-card-header__content .weather .temperature,
.header-search-bar .searchbar-inner .search-options .dropdown-legacy .items.active .item:hover,
.hourly-card-nfl-header .temp,
