 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

.high
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

.half-day-card .quarter-day-ctas,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/* Misc 
_____________________________________________________________________________________________________*/

.air-quality-chart__chart .y-axis-line,
.air-quality-chart__chart .x-tick-line,
.air-quality-chart__chart .y-tick-line line
{
    stroke-dasharray: 4;
    stroke-width: 1;
    stroke: #f1eaea;
    opacity: .5;
}

.aq-particles .air-quality-particles .particles.active-particles {
    fill: #05faff;
}

.air-quality-chart__chart .plotline-line {
    stroke: #71000091;
}

.aq-particles .air-quality-particles .particles {
    fill: #a35f5f;
}

}

