css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.deviantart.com") {

.alt2, .even-beacon {
background-color: #2698c5!important;
}

.damncrc-chat-window {
border-color: #30c1de!important;
border-width: 4px!important;
border-style: groove!important;
}

.damncr .msg a {
color: #1f7cb3!important;
}

.damn .alt2 {
background-color: #d5e7f3!important;
background-image: none!important;
background-repeat: repeat;
background-attachment: scroll;
color: #1f7cb3!important;
}

body.chatroom div.bubbleview, .alt0 {
background-color: #1f7cb3!important;
background-image: none;
background-repeat: repeat;
background-attachment: scroll;
color: #fff!important;
}

.alt0 {
background-color: #1f7cb3!important;
color: #fff!important;
}

.alt0 a, .alt2 a, .alt1 a {
color: #fff!important;
}

.damncrc-topic {
background-image: url('http://i234.photobucket.com/albums/ee170/dcz-dacss/admin/tigradv.png')!important;
background-color: #2fbfdc!important;
border-style: solid;
border-color: #065d90!important;
border-width: 1px!important;
border-bottom-width: 0px!important;
padding:5px!important;
}

.damncrc-input input, .damncrc-input textarea {
color: #065d90!important;
}

.damncr .join, .damncr .part {
color: #688ca1!important;
}

.damnc-tabbar strong, .damnc-tabbar a {
text-decoration: none;
color: #fff!important;
background-color: transparent;
background-image: url(http://i234.photobucket.com/albums/ee170/dcz-dacss/admin/tabsblue.png)!important;
}

.damnc-tabbar strong i, .damnc-tabbar a i {
text-decoration: none;
background-color: transparent;
background-image: url(http://i234.photobucket.com/albums/ee170/dcz-dacss/admin/tabsblue.png)!important;
}

.damnc-tabbar-outer {
border-top-width: 0px!important;
padding-top: 0px!important;
background-color: #2698c5!important;
border-bottom-width: 1px;
border-bottom-style: solid;
border-bottom-color: #fff!important;
}

/* rockdock shiz */

body.chatroom #top {
background-color: #2698c5!important;
background-image: none!important;
background-repeat: repeat-x;
background-attachment: scroll;
background-position: left bottom;
}

body.chatroom #logindock i.c i, body.chatroom #logindock div.ch-ctrl {
border-top-color: #2698c5!important;
border-bottom-color: #2698c5!important;
}

body.chatroom #logindock div.ch-ctrl {
background-color: #0f3349!important;
}

body.chatroom #logindock div.text {
line-height: 26.5px;
vertical-align: middle;
margin-left: 7px;
color: #688ca1!important;
font-size: 15px;
padding-right: 8em;
}

body.chatroom #logindock div.text a, body.chatroom #logindock div.text span.a {
color: #d5e7f3!important;
text-decoration: none;
}

body.chatroom #logindock div.text a.blend {
color: #688ca1!important;
}

body.chatroom #logindock div.text a, body.chatroom #logindock div.text span, body.chatroom #logindock div.text label {
font-size: 7.5pt!important;
letter-spacing: 2px!important;
}

body.chatroom #logindock div.text a:hover, body.chatroom #logindock div.text a:focus {
color: #31c5e0!important;
text-decoration: none;
}

body.chatroom #logindock span.glink a, body.chatroom #logindock span.logout a, body.chatroom #logindock span.inputter {
background-color: transparent;
background-image: url(http://i234.photobucket.com/albums/ee170/dcz-dacss/admin/rdblu.png)!important;
background-repeat: no-repeat;
}

body.chatroom #logindock div.text a:hover, body.chatroom #logindock div.text a:focus {
color: #ffffff;
text-decoration: none;
}

body.chatroom #logindock span.gspecial a, body.chatroom #logindock span.gspecial a:focus {
text-indent: 37px;
color: #768e81;
}

body.chatroom #logindock span.gspecial a:hover {
color: #869e91;
}

}
