css:
@-moz-document domain('youtube.com') {
  html[dark], [dark] {
    --yt-spec-base-background: #36393e !important;
    --yt-spec-menu-background: #272a2c !important;
    --yt-spec-text-secondary: #eaeaea !important;
  }

  html[darker-dark-theme][dark] {
    background-color: #36393e !important;
  }

  div.style-scope.ytd-masthead {
      background-color: #3b3f44 !important;
    }

  #container.ytd-searchbox {
    background-color: #2c2e32 !important;
  }

  #contents.ytd-rich-grid-renderer {
    background:  #36393e !important;
  }

  #scroll-container.ytd-feed-filter-chip-bar-renderer {
    background:  #36393e !important;
  }

  ytd-mini-guide-renderer.ytd-app {
    background:  #36393e !important;
  }

  ytd-mini-guide-entry-renderer[guide-refresh] a.ytd-mini-guide-entry-renderer {
    background:  #36393e !important;
  }

  .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--filled {
    color: #fff !important;
    background-color: #5865f2 !important;
  }

  .sbdd_b {
    background: #272a2c !important;
    border: #272a2c !important;
  }

  .sbsb_d {
    background: #2d2f32 !important;
  }

  .sbsb_a {
    background: #272a2c !important;
    border: #272a2c !important;
  }

  .gsfs {
    color: #fff !important;
  }

  .sbfl_b {
    background: #272a2c !important;
  }

  .sbqs_c:before {
    background: no-repeat url("data:image/svg+xml;base64,") !important;
  }

  .ytp-swatch-background-color {
    background-color: #5865f2;
  }
}
