css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("rs.4chan.org") {
* {
	font-family: Sans !important;
	font-size: 8pt !important;
	font-weight:normal !important;
	color: #EFEFEF !important;
}

body {
	background: #111 !important;
}

a {
	color: #AAA !important;
}

a:hover {
	color: #46F !important;
}

hr, #footer, #navtopr, .logo {
	display:none !important;
}

#navtop {
	width: 100% !important;
	padding: 0px !important;
	margin: 0px !important;
	text-align: center !important;
	top: 0 !important;
	z-index: 30;
}

.postblock {
	background: #000 !important;
	border: 1px solid #333 !important;
}

.pages {
	background: #000 !important;
	border: none !important;
	-moz-border-radius: 3px !important;
        color: #333 !important
}
}

@-moz-document domain("zip.4chan.org"),
domain("orz.4chan.org"),
domain("boards.4chan.org"),
domain("tmp.4chan.org"),
domain("bin.4chan.org"),
domain("img.4chan.org"),
domain("cgi.4chan.org") {

* {

	font-family: Sans !important;
	font-size: 8pt !important;
	font-weight:normal !important;
}

body {
	background: #000 !important;
	color: #666 !important;
	margin: 0px !important;
	padding: 0px !important;
}

html {
	margin: 0px !important;
	padding: 0px !important;
}
.unkfunc{color: #aaa !important;}
a {
	color: #333 !important;

}

a:hover {
	color: #fff !important;
}

hr, #filter-button, #option-button, #ft,  #hd, .deletebuttons, #footer center, #navbotr, #navtopr, img[src*="/dontblockthis"], .logo, #ad, a[href*="http://www.4chan.org/advertise/"], .rules, iframe[src="http://www.4chan.org/rotating.php"], img[src*="http://content.4chan.org/tmp"],
td[align*="right"], .doubledash, iframe {
	display:none !important;
}

#navtop, #navbot {
	width: 100% !important;
	padding: 0px !important;
	margin: 0px !important;
	text-align: center !important;
	color: #333 !important;
	top: 0 !important;
	z-index: 30;
}

form {
	margin: 1em !important;
}

.inputtext, textarea {
	width: 295px !important;
}

.postblock {
	background: #000 !important;
	border: 1px solid #333 !important;
	color: #333 !important;
}

.reply {
	background-color:#010101 !important;
	padding: .2em !important;
	border: 1px solid #333 !important;
	color: #666 !important;
        -moz-border-radius: 0px 7px 0px 7px !important;
}

.replyhl {
	background:#0a0a0a !important;
	padding: .2em !important;
	border: 1px solid #333 !important;
	color: #eee !important;
	-moz-border-radius: 0px 7px 0px 7px !important;
}

.postername, .commentpostername, .postertrip, .abbr {
	color: #333 !important;
        text-shadow: #666 0px 0px 2px !important;
}
.omittedposts{color: #333 !important;font-size:8px !important}
.filetitle, .replytitle, .filesize {
	color: #333 !important;
	font-size: inherit;
}

th {
	background: #010101 !important;
	padding: .2em !important;
	font-size: small;
	width: 200px !important;
}


.boxbar {
	background: #000 !important;
}

.box-outer, .box-inner {
	border: none !important;
	background: #000 !important;
}

.pages {
	background: #000 !important;
	border: none !important;
	-moz-border-radius: 3px !important;
        color: #333 !important
}
.pages td {
        color: #333 !important
}
#recent-images li{
	background: #000 !important;
}

pre, textarea {
	background: #000 !important;
	font-size: inherit ! important;
	border: 1px solid gray !important;
}

INPUT, input {
	color: white !important;
	background: #000 !important;
}
a[href*="mailto:sage"]:after,
a[href*="mailto:SAGE"]:after {
	content: " (sage)" !important;
}


div.postblock {
text-align: center !important;
background: #000 !important;
border-bottom: 1px solid #909090 !important;
padding: 2px !important;
font: 12px Calibri !important;
font-weight: lighter !important;
color: #909090 !important;
-moz-border-radius: 5px 5px 0px 0px !important;
}

SPAN[class="4chan_win_header_buttons"] {
top: 2px !important;
}

[id^="4chan_ext"][class="reply"], .reflinkpreview {
background: #000 !important;
border: 1px solid #909090 !important;
-moz-border-radius: 5px !important;
}

postarea {
padding: 0px !important;
margin: 0px !important;
width: 100% !important;
}

.postarea textarea, .postarea .inputtext, .postarea input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]):not([name="sub"]) {
color: #909090 !important;
background: #000 !important;
border: 1px solid #909090 !important;
-moz-appearance: none !important;
font: 12px Calibri !important;
padding: 0px !important;
width: 300px !important;
}

.postarea select[name="filetag"] {
color: #909090 !important;
background: #000 !important;
border: 1px solid #909090 !important;
-moz-appearance: none !important;
font: 12px Calibri !important;
padding: 0px !important;
width: 300px !important;
}

.postarea select[name="from"] {
color: #909090 !important;
background: #000 !important;
border: 1px solid #909090 !important;
-moz-appearance: none !important;
font: 12px Calibri !important;
padding: 0px !important;
width: 302px !important;
}

td > .inputtext {
height: inherit !important;
padding: 1px !important;
}

.inputtext:focus {
border: 1px solid #5a5a5a !important;
}

input.inputtext {
height: inherit !important;
}

.postarea td.postblock {
display: none !important;
}

td.postblock {
background: #000 !important;
border: 1px solid #909090 !important;
color: #909090 !important;
font: 10px Calibri !important;
text-align: center !important;
padding: 0px 5px !important;}

.postarea table, .postarea span, .postarea span a {
color: #909090 !important;
font: 12px Calibri !important;
padding: 0px !important;
margin: 0px !important;
}

.postarea td.rules {
display: none !important;
}

.postarea small {
display: none !important;
}

.postarea input[value="Submit"], .postarea input[type="button"]:not([value="Report"]), .postarea input[value="Go"] {
border: 1px solid #909090 !important;
background: #000 !important;
color: #909090 !important;
font : 10px Calibri !important;
text-transform: uppercase !important;
text-align: center !important;
padding: 0px !important;
-moz-appearance: none !important;
height: 16px !important;
width: 50px !important;
margin: -1px 0px 0px 1px !important;
}

.postarea input[name="sub"] {
width: 249px !important;
}

.postarea input[value="Go"] {
margin-top: -1px !important;
height: 18px !important;
}

.postarea input[value="Submit"] {
display: inline-block !important;
margin-left: -1px !important;
position: relative !important;
top: 0px !important;
padding: 0px !important;}



.postarea input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]):not([name="sub"]):not([name="name"]):not([name="email"]):not([name="com"]):not([name="pwd"]) {
width: 249px !important;
}


.hidethread, .unhidethread, .watchthread, .expandthread, .quickreply, .hidewatchedthreads, .refreshwatchedthreads, .restorewatchedthreads, * html 
.hidethread, .unhidethread, .watchthread, .expandthread, .quickreply, .hidewatchedthreads, .refreshwatchedthreads, .restorewatchedthreadsimg, img[src^="chrome://4chan/skin/buttons/"], img[alt="Locked"], img[alt="closed"], img[alt="sticky"] {
background-image: url('http://img193.imageshack.us/img193/5427/extbuttons.png') !important; }
img[src^="chrome://4chan/skin/buttons/"], img[alt="Locked"], img[alt="closed"], img[alt="sticky"] { height: 0px !important; margin: 0px 2px 0px 2px !important; padding-top: 14px !important; width: 14px !important; }
[src$="arrow_up.png"] { background-position: 0px 0px !important; }
[src$="arrow_down.png"] { background-position: 0px 14px !important; }
[src$="arrow_right.png"] { background-position: -14px 0px !important; }
[src$="arrow_left.png"] { background-position: -14px 14px !important; }
[src$="arrow_down2.png"] { background-position: -56px 14px !important; }
[src$="post_expand_minus.png"] { background-position: -28px 14px !important; }
[src$="post_expand_plus.png"] { background-position: -28px 0px !important; }
[src$="stop.png"] { background-position: -56px 0px !important; }
[src$="quote.png"] { background-position: -42px 0px !important; }
[src$="cross.png"] { background-position: -42px 14px !important; }
[src$="empty.png"] { background-position: 14px 0px !important; }
img[alt="Locked"], img[alt="closed"] { background-position: -70px 0px !important; }
img[alt="sticky"] { background-position: -70px 14px !important; }


input[type="checkbox"] {
-moz-border-radius: 5px !important;
border: 1px solid #909090 !important;
background: #151515 !important;
-moz-appearance: none !important;
color: #5a5a5a !important;
height: 10px !important;
width: 10px !important;
margin-right: 5px !important;
}

input[type="checkbox"]:checked {
-moz-border-radius: 5px !important;
border: 1px solid #909090 !important;
background-color: #5a5a5a !important;
-moz-appearance: none !important;
color: #5a5a5a !important;
height: 10px !important;
width: 10px !important;
margin-right: 5px !important;
}

.replyhl input[type="checkbox"] {
-moz-border-radius: 5px !important;
border: 1px solid #909090 !important;
background-color: #5a5a5a !important;
-moz-appearance: none !important;
color: #5a5a5a !important;
height: 10px !important;
width: 10px !important;
margin-right: 5px !important;
}

.replyhl input[type="checkbox"]:checked {
-moz-border-radius: 5px !important;
border: 1px solid #909090 !important;
background-color: #151515 !important;
-moz-appearance: none !important;
color: #151515 !important;
height: 10px !important;
width: 10px !important;
margin-right: 5px !important;
}

input[type="submit"] {
-moz-border-radius: 5px !important;
border: 1px solid #909090 !important;
background: #151515 !important;
-moz-appearance: none !important;
color: #5a5a5a !important;
height: 20px !important;
width: 75px !important;
margin-right: 5px !important;
}

.postarea input[type="file"] {
-moz-border-radius: 5px !important;
border: 1px solid #909090 !important;
background: #151515 !important;
-moz-appearance: none !important;
color: #5a5a5a !important;
height: 20px !important;
width: 75px !important;
margin-right: 5px !important;
}

}
