css:
@-moz-document domain(windows96.net) {
.titlebar-title-disabled {
    color: silver;
    background: transparent;
}
.w96-tab-control>.content {
    flex: 1;
    border-top: none;
    border-left: none;
    border-right: none;
    border-bottom: none;
    border-radius: 7px;
    border-top-left-radius: 0px;
    box-shadow: none;
    margin: 5px;
    margin-top: 0px;
    padding: 3px;
    background: #3232328c;
    box-sizing: border-box;
}.w96-tab-control>.pages>button {
    border-top: none;
    border-left: none;
    border-right: none;
    border-bottom: none;
    box-shadow: none;
    margin-bottom: 0px;
    margin-right: 1px;
    padding: 2px;
    padding-top: 3px;
    padding-bottom: 1px;
    height: 19px;
    font-size: 11px;
    font-family: px_sans_serif;
    background: rgba(0,0,0,0);
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    padding-left: 8px;
    font-family: "Segoe UI Variable Text";
    padding-right: 8px;
    color: white;
    position: relative;
    background: #3232328c;
    top: 1px;
    outline: 0;
    cursor: url(https://windows96.net/system/resource/cursors/default.png),auto;
}
.ctrl-app>.view>.p2>.item {
    width: 170px;
    margin-bottom: 10px;
    font-family: "Segoe UI Variable Text";
    display: inline-block;
}
.ctrl-app>.view>.p1>.box>.title {
    padding: 5px;
    box-sizing: border-box;
    text-shadow: none;
    font-family: "Segoe UI Variable Text";
    letter-spacing: 1px;
    margin-left: 1px;
}
.w96-footer .cell {
    padding-left: 2px;
    padding-right: 2px;
    border-left: none;
    border-bottom: none;
    border-top: none;
    border-right: none;
    width: 100%;
    font-family: "Segoe UI Variable Text";
}
.ctrl-app>.view>.p1>.box>.items {
    background: #fff;
    color: #000040;
    padding: 5px;
    font-family: "Segoe UI Variable Text";
    border-bottom-left-radius: 7px;
    box-sizing: border-box;
    border-bottom-right-radius: 7px;
}
.ctrl-app>.view>.p1>.box {
    border: 1px solid #3232328f;
    margin-bottom: 5px;
    border-radius: 7px;
}
.ctrl-app>.view>.p1 {
    width: 155px;
    background: transparent;
    color: #fff;
    padding: 5px;
    box-sizing: border-box;
}
.ctrl-app>.view>.p2>.cat-marker::after {
    display: block;
    position: absolute;
    width: 100%;
    height: 0.5px;
    content: " ";
    background: #ffffff3b;
    transform: translateX(-10px) translateY(3px);
}
.ctrl-app>.view>.p2>.cat-marker {
    text-shadow: none;
    letter-spacing: 1px;
    font-family: "Segoe UI Variable Text";
    width: 40%;
    margin-bottom: 15px;
    transform: translateX(-10px);
    padding-left: 10px;
}
::-webkit-scrollbar {
    width: 8px;
}
::-webkit-scrollbar-thumb {
    background-color: #00000073;
    box-shadow: none;
    border-radius: 999px;
}
::-webkit-scrollbar-track {
    background-image: none;
}
.ctrl-app>.view>.p2 {
    width: 100%;
    flex: 1;
    padding: 10px;
    box-sizing: border-box;
    background: #3232328c;
    border-top-left-radius: 7px;
    border-bottom-left-radius: 7px;
    overflow-y: auto;
}
.w96-tab-control>.pages>button:after {
    box-shadow: none;
    background: transparent;
    width: 0%;
    border: none;
}
.w96-select {
    border-top: none;
    border-left: none;
    border-right: none;
    background: #3232328c;
    color: white;
    border-radius: 4px;
    border-bottom: none;
    box-shadow: none;
    box-sizing: border-box;
    cursor: url("https://windows96.net/system/resource/cursors/default.png"),auto;
    height: 20px;
}
.wexplorer-app .nav-bar>.go:after {
    background-image: url("https://windows96.net/system/resource/app/explorer/go.png");
    content: "Go";
}
.wexplorer-app:not(.cdlg-ofd) .nav-bar:not(.compact-toolbar)>.go {
    box-shadow: none;
}
.text {
    font-family: "Segoe UI Variable Text";
}
.window-html-content>.mbox>.content>.text {
    width: 100%;
    margin-right: 16px;
    margin-top: 16px;
    margin-bottom: 16px;
    font-family: "Segoe UI Variable Text";
    font-size: 11px;
    user-select: text;
}
.w96-menubar>.root-item.selected {
    box-shadow: none;
    background: #323232bd;
}
.w96-button {
    line-height: 1.35;
    padding: 5px;
    background-color: rgba(0,0,0,0);
    font-family: "Segoe UI Variable Text";
    font-size: 11px;
    padding-top: 4px;
    padding-bottom: 3px;
    padding-left: 10px;
    padding-right: 10px;
    cursor: url("https://windows96.net/system/resource/cursors/default.png"),auto;
    background: #3232325e;
    box-shadow: none;
    color: white;
    border: none;
    border-radius: 4px;
    outline: 0;
}
.w96-menubar>.root-item:hover {
    box-shadow: none;
    background: #32323259;
}
.w96-toolbar>.item {
    display: inline-flex;
    vertical-align: top;
    flex-direction: column;
    border-radius: 7px;
}
.w96-toolbar .vseparator {
    height: 100%;
    border: none;
    border-left: none;
    margin-left: 0px;
    margin-right: 0px;
    margin-top: 0px;
    margin-bottom: 0px;
    height: calc(100% - 4px);
    display: inline-block;
    box-shadow: none;
}
.wexplorer-app .w96-toolbar>.item:hover {
    background: #32323259;
    box-shadow: none;
}
.wexplorer-app .nav-bar {
    display: flex;
    align-items: center;
    box-shadow: none;
    height: 24px;
}
.w96-ui-inset {
    border: none;
    box-shadow: none;
    margin: 0px;
}.w96-toolbar {
    width: 100%;
    font-family: "Segoe UI Variable Text";
    font-size: 11px;
    box-shadow: none;
    padding-top: 0px;
    box-sizing: border-box;
    user-select: none;
    overflow: hidden;
}

.titlebar-title {
    display: inline-block;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
    max-width: calc(100% - 55px);
    position: relative;
    top: 2px;
    text-shadow: none;
    letter-spacing: 1px;
    font-weight: 400;
    margin-top: -3px;
    padding-left: 3px;
    box-sizing: border-box;
    line-height: 1em;
    height: 16px;
}
.window-dlg:not(.ui-sortable-helper) {
    display: none;
    position: absolute;
    top: 100px;
    left: 100px;
    min-height: 19px;
    min-width: 100px;
    width: 640px;
    height: 480px;
    backdrop-filter: blur(20px);
    background-color: #32323275;
    z-index: 10;
    padding: 2px;
    border: none;
    border-left: 1px solid #32323257;
    color: white;
    border-radius: 7px;
    border-top: 1px solid #32323257;
    border-right: 1px solid #32323257;
    box-shadow: none;
    border-bottom: 1px solid #32323257;
    flex-direction: column;
}
.titlebar {
    background: transparent;
    color: #fff;
    font-family: "Segoe UI Variable Text";
    font-size: 11px;
    padding: 3px;
    padding-bottom: 2px;
    font-weight: 100;
    line-height: 1.3;
    box-sizing: border-box;
    user-select: none;
}
.w96-menubar {
    width: 100%;
    height: fit-content;
    color: #fff;
    line-height: 8px;
}
.w96-menubar>.root-item {
    font-family: "Segoe UI Variable Text";
    font-size: 11px;
    padding-left: 10px;
    padding-right: 10px;
    display: inline-block;
    user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -webkit-user-select: none;
    cursor: url("https://windows96.net/system/resource/cursors/default.png"),auto;
    line-height: 28px;
    margin-bottom: 1px;
    margin-top: 1px;
}
.wcontext-menu>.item {
    color: #b5b5b5;
    text-overflow: ellipsis;
    overflow: hidden;
    border-radius: 7px;
    padding-right: 32px;
    padding-left: 24px;
    font: 12px
px
 "Segoe UI Variable Text";
    padding-top: 1px;
    line-height: 16px;
}
.taskbar-task:focus {
    outline-style: none;
    outline-width: 1px;
    outline-offset: -1px;
}

.wcontext-menu>.item:not([disabled]):hover, .wcontext-menu>.item.selected {
    color: var(--select-color-secondary);
    background-color: #ffffff1a;
    text-shadow: none;
}
.taskbar-task_active {
    border-top: none;
    background: #00000082;
    border-left: none;
    border-right: none;
    border-bottom: none;
    box-shadow: none;
}
.wcontext-menu {
    background: #3232328c;
    border-left: none;
    border-radius: 7px;
    border-top: none;
    backdrop-filter: blur(20px);
    border-right: none;
    padding: 4px;
    border-bottom: none;
    box-shadow: none;
    font: 11px "Segoe UI Variable Text";
    display: inline-block;
    position: absolute;
    display: flex;
    flex-direction: column;
    min-width: 115px;
    max-width: 225px;
    transform: translateX(15px);
    padding: 6px;
}
.taskbar-task-icon {
    height: 16px;
    width: 16px;
    margin-top: auto;
    margin-bottom: auto;
    margin-left: 10px;
    background-size: contain !important;
}
.taskbar-task-text {
    margin-top: 10px;
    margin-bottom: auto;
    font-family: "Segoe UI Variable Text";
    margin-left: 5px;
    margin-right: 4px;
    flex: 1;
    line-height: 1.5em;
    text-overflow: ellipsis;
    overflow: hidden;
    word-break: break-all;
    white-space: nowrap;
    max-height: 16px;
}
.taskbar-task {
    display: inline-flex;
    min-width: 160px;
    user-select: none;
    cursor: url("https://windows96.net/system/resource/cursors/default.png"),auto;
    margin-right: 0px;
    vertical-align: top;
    height: 100%;
    width: fit-content;
    border-style: ridge;
    background-repeat: repeat;
    font-family: px_sans_serif;
    font-size: 11px;
    box-sizing: border-box;
    border-radius: 0px;
    border-top: none;
    border-bottom: none;
    border-left: none;
    color: white;
    box-shadow: none;
    border-right: none;
    text-shadow: none;
    max-width: 160px;
    word-break: break-all;
    padding-left: 0px;
    padding-right: 0px;
    height: 40px;
    margin-top: 3px;
    border-radius: 7px;
    text-align: left;
    outline: 0;
    background-color: #c0c0c059;
    min-width: 0px;
    flex: 1;
	margin-right: 4px;
}
	.user-taskbar {
    height: 48px;
    width: 100%;
    z-index: 10000000;
    box-sizing: border-box;
    background: #3232328c;
    border-style: ridge;
    display: flex;
    max-height: 48px;
    flex-direction: row-reverse;
    backdrop-filter: blur(20px);
    position: fixed;
    bottom: 0;
    border-top: none;
    padding-top: 0px;
    border: none;
    box-shadow: none;
}

.taskbar-notify {
    display: flex;
    float: right;
    font-family: "Segoe UI Variable Text";
    font-size: 14px;
    font-weight: 200;
    height: 100%;
    border-left: none;
    border-top: none;
    border-right: none;
    border-bottom: none;
    color: white;
}

.user-desktop>.build-info {
    font-family: monospace;
    color: #fff;
    position: fixed;
    bottom: 48px;
    right: 0;
    font-family: "Segoe UI Variable Text";
    z-index: 1;
    opacity: 30%;
    transition: opacity 1s;
}

.user-desktop>.build-info {
    color: #fff;
    position: fixed;
    bottom: 48px;
    right: 0;
    font-family: "Segoe UI Variable Text";
    font-size: 13px;
    z-index: 1;
    opacity: 30%;
    transition: opacity 1s;
}

.start_button {
    height: 48px;
    display: inline-block;
    border-radius: 0px;
    cursor: url("https://windows96.net/system/resource/cursors/default.png"),auto;
    background-image: url("https://windows96.net/system/resource/shell/start-icn.png");
    background-repeat: no-repeat;
    background-position-y: center;
    width: 48px;
    background-color: transparent;
    border: none;
    box-shadow: none;
    background-position-x: center;
}

.start_button:after {
  content: "";
}

.start_button.sb-active {
    box-shadow: none;
    background-color: #303030;
}

.w96-button:focus {
    outline-style: none;
    outline-width: 1px;
    outline-offset: -4px;
}

.shell36-start-menu {
    left: 10px;
    border-radius: 7px;
    position: fixed;
    backdrop-filter: blur(20px);
    background-color: #3232328c;
    color: white;
    text-shadow: 0px;
    z-index: 10000000;
    -webkit-animation-duration: .5s;
    animation-duration: .5s;
    font-size: 11px;
    border: none;
    box-shadow: none;
    font-family: px_sans_serif;
    animation-duration: .25s !important;
    display: flex;
    transform: translateY(-33px);
}

.shell36-start-menu .sidebar {
    width: 21px;
    content: "Windows 97";
    border-bottom-right-radius: 7px;
    text-orientation: sideways;
    border-top-right-radius: 7px;
    writing-mode: vertical-lr;
    transform: rotate(180deg);
    color: #fff;
    font-weight: 700;
    font-size: 15px;
    background: #3232325c;
    padding-left: 2px;
    padding-top: 5px;
    font-family: "Segoe UI Variable Text";
    box-sizing: border-box;
}

.shell36-start-menu>.item-container>.item {
    height: 36px;
    min-width: 148px;
    border-radius: 7px;
}

.shell36-start-menu>.item-container>.item {
    height: 36px;
    min-width: 148px;
    font-family: "Segoe UI Variable Text";
    border-radius: 7px;
}

.shell36-start-menu>.item-container>.item:hover, .shell36-start-menu>.item-container>.item.active {
    background-color: #ffffff30;
    color: var(--select-color-secondary);
}

.w96-ui-inset-separator {
    display: block;
    width: 100%;
    border-top: 0.5px solid #ffffff38;
    border-bottom: none;
}

.shell36-start-menu>.item-container {
    flex: 1;
    padding: 6px;
}
.user-taskbar .separator {
    background: #00000000;
    width: 0px;
    border-right: none;
}
}
