@-moz-document domain("developer.mozilla.org") {
/* Site Settings */

* {
	text-decoration: none!important;
}

.button.action .button-wrap
{
	text-transform: capitalize!important;
}

#specifications > tr,
#specifications th,

.button.clear-search-button, .button.search-button,
.button.hover .button-wrap, .button:hover .button-wrap,
.document-toc-link.document-toc-link:not(.button),

:focus
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

/*___ Fonts ________________________________________________________________________*/

.code-example,
.logo,

code, pre
{
	font-size: 28px!important;
}

.sidebar code, .sidebar summary,

body,
code[class*=language-],
p,
pre[class*=language-]
{
	font-size: 1.85rem!important;
}

.document-toc-list.document-toc-list,
.latest-news .news-item .news-date,
.recent-contributions .request-item .request-date,
.sidebar ol
{
	font-size: 1.2rem!important;
}

selector
{
	font-size: 15px!important;
}

selector
{
	line-height: 25px!important;
}

/*___ Remove Items _________________________________________________________________*/

.featured-articles .article-tile p:after,
.theme-switcher-menu
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.top-navigation-main {
    margin-left: 40rem;
}

x.article-actions-container {
    margin-top: 1.5rem;
}

.main-wrapper .main-content {
    margin-left: -9rem;
}

/*___ Margins ______________________________________________________________________*/

.metadata
{
	margin: 12px!important;
}

selector
{
	margin: 3px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

selector
{
	padding: 12px!important;
}

.button
{
	padding: 3px 8px!important;
}

/*___ Heights ______________________________________________________________________*/

.example-choice-list.live,
x.sidebar
{
	max-height: none!important;
}

.example-choice-list.live
{
	height: auto!important;
}

.interactive {
    height: 42rem;
}

.top-navigation .container {
    height: 15rem!important;
}

/*___ Widths _______________________________________________________________________*/

.container,
.featured-articles .tile-container,
.homepage-hero section,
.latest-news,
.recent-contributions,
.main-wrapper,

ul.main-menu
{
	max-width: none!important;
}

selector
{
	width: auto!important;
}

.homepage-hero section,
.latest-news,
.recent-contributions,
.main-wrapper
{
	width: 98%!important;
}

.document-page .sidebar {
    width: 79%!important;
}
}

