@-moz-document domain("developer.mozilla.org") {
/* Font Settings */

a[href],
td span
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/*__________________________________________________________________________________*/

code[class*=language-], pre[class*=language-],
button,
html,
li,
p
 {
	color: #fff!important;
}

/*___ Khaki ________________________________________________________________________*/

.sidebar summary,
.standard-table:first-of-type tr:first-child,

dt,
h1,
h2
 {
	color: #F0E890!important;
}

/*___ Burlywood ____________________________________________________________________*/

.output-header h4,
h3,
h4
 {
	color: #e0b880!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/*___ Blue _________________________________________________________________________*/

.latest-news .news-item .news-date,
.recent-contributions .request-item .request-date,

time
 {
	color: #07beff!important;
}

/*___ Green ________________________________________________________________________*/

selector
 {
	color: #07ff24!important;
}

/*___ Red __________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #641a1a!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f;
	opacity: 1;
}

/*___ Misc _________________________________________________________________________*/


.token.entity,
.token.operator,
.token.url,
.token.punctuation
{
	color: #fff!important;
}

.token.attr-name,
.token.builtin,
.token.char,
.token.inserted,
.token.selector,

em
{
    color: #01ff9f;
}

.token.string,
.language-css .token.string,
.style .token.string
{
	color: #af0!important;
}

.token.atrule, .token.attr-value, .token.keyword
{
	color: #00b3ff!important;
}

code,
strong
{
	color: #ffcb17!important;
}

.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted
{
	color: #ff6cbe!important;
}


}

@-moz-document domain("developer.mozilla.org") {
/* Background Settings */

body,
html {
	background: #000!important;
}

selector
{
	background-image: none;
}

/*___ Background Gray ______________________________________________________________*/

.page-header-main              
{
	background: #060505!important;
	background-color: #060505!important;
}

.article-actions-container,
.breadcrumb-locale-container
{
    background-color: #101010!important;
}

/*___ Background 0000 ______________________________________________________________*/

selector
{
	background: #0000!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.bc-table tbody tr:nth-child(2n),
.bc-table,
.button.clear-search-button,
.button.hover .button-wrap,
.button:hover .button-wrap,
.header-search,
.hero-content h1,
.recent-contributions .request-item:nth-child(odd),
.properties td,
.latest-news .news-item:nth-child(odd),
.properties th,

code,
pre,
table td,
table th,
ul.main-menu
{
	background-color: #0000!important;
}

/*__ Blue __________________________________________________________________________*/

.button.action:not(.search-button)
{	
	background: radial-gradient(circle, #1b8bc8a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;
	z-index: 1!important;
}

.button.action:not(.search-button)::before
{
	background: radial-gradient(circle, #000 0%, #1b8bc8 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .8s linear!important;
	z-index: -1!important;
}

.button.action:not(.search-button):hover::before
 {
  opacity: 1!important;
}

/*__ Red __________________________________________________________________________*/

selector
{
	background-image: linear-gradient(to bottom right, #da0404, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
}

selector:hover
{
	background-image: linear-gradient(to bottom right, #eb1515, #000)!important;
	color: #ff0!important;
}

selector:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
}

/*__ Orange ________________________________________________________________________*/

selector
{
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
}

selector:hover
{
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

selector:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
}

/*___ Green ________________________________________________________________________*/

selector
{
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #fff!important;
}

selector:hover
{
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

selector:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
}

/*___ Plain Black __________________________________________________________________*/

selector
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.featured-articles .article-tile,
.interactive,
.metadata,

ul.main-menu li ul
{
	background-color: #1c1c1c!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
}

.featured-articles .article-tile:hover,
.interactive:hover,
.metadata:hover,

ul.main-menu li ul:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Blue Gradient _________________________________________________________________*/

.mdn-cta-container
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

.callout
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Green Gradient ________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Yellow Gradient _______________________________________________________________*/

.notecard.note
{
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

.notecard.note a,
.notecard.note {
	letter-spacing: 1px!important;
	text-shadow: 3px 3px #000!important;
}

/*__ Orange Gradient _______________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Code ___________________________________________________________________________*/

code
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) no-repeat!important;
	background-size: 100%!important;
	background-repeat: round!important;
}

/*___ Highlight Background _________________________________________________________*/

.bc-table tbody tr:hover,
.document-toc-link.document-toc-link:not(.button)[aria-current]:not([aria-current=""]):not([aria-current=false]),
.sidebar em,
.sidebar summary:hover,
.standard-table tr:not(:first-child):hover,

ul.main-menu li ul a:hover
{
	background: linear-gradient(to right, #0a3851, #0000)!important;
	border-left-color: #434343!important;	
	transition-duration: .33s!important;
}

table.properties tr:hover
{
	background: linear-gradient(to right, #041924a1, #0000)!important;
	border-left-color: #434343!important;	
	transition-duration: .33s;
}

/*__ Blue Gadient _________________________________________________________________________*/

.document-toc-list.document-toc-list li,
.sidebar summary,

table.properties tr
{	
	background: linear-gradient(to right, #0000, #0000)!important;
	border: none!important;
	color: #fff!important;
	opacity: 1!important;
	position: relative!important;	
	z-index: 1!important;
}

.document-toc-list.document-toc-list li::before,
.sidebar summary::before,

table.properties tr::before
{
	background: linear-gradient(to right, #0a3851, #0000)!important;
	bottom: 0;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: all .8s ease-in-out!important;
	z-index: -1!important;
}

.document-toc-list.document-toc-list li:hover::before,
.sidebar summary:hover::before,

table.properties tr:hover::before
 {
  opacity: 1!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.top-navigation
{
	background: linear-gradient(to bottom, #242323 0%, #000 100%)!important;
}

.page-footer
{
	background: linear-gradient(to top, #242323 0%, #000 80%)!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.latest-news .news-item:hover,
.recent-contributions .request-item:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}

/*___ Radial Background _________________________________________________________*/

selector
{
	background: radial-gradient(ellipse at center, #07ed07 0%,#0000 70%)!important;
}

selector
{
	background: radial-gradient(ellipse at center, #9a9d00 0%,#0e0e0e 70%)!important;
}

selector
{
	background: radial-gradient(ellipse at center, #00809d 0%,#0e0e0e00 70%)!important;
}

}

@-moz-document domain("developer.mozilla.org") {
/* Image Settings */

.blog-feed ul li,
.code-example li,
.featured-articles .article-tile,
.main-page-content ol li, .main-page-content ul li,
.recent-contributions .request-item,
.recent-contributions,
.latest-news,

dd,
p,
table tbody tr:not(:first-child)
 {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.blog-feed ul li:hover,
.code-example li:hover,
.featured-articles .article-tile:hover,
.main-page-content ol li:hover, .main-page-content ul li:hover,
.latest-news:hover,
.recent-contributions:hover,
.recent-contributions .request-item:hover,

dd:hover,
p:hover,
table tbody tr:not(:first-child):hover
 {
	filter: brightness(100%)!important;
}

ul.main-menu li ul li:first-child a:hover {
	border-top-left-radius: 22px;
}

ul.main-menu li ul li:last-child a:hover {
	border-bottom-left-radius: 22px;
}

/*__________________________________________________________________________________*/

img
 {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img
 {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

}

@-moz-document domain("developer.mozilla.org") {
/* New Icons */

/* Logo ___________________________________________________________________________*/

svg:not(:root) {
    overflow: visible!important;
}

.logo svg
{
	background: url(https://i.ibb.co/dp01Jy5/MDN-Web-Docs-Logo-wine.png) no-repeat!important;
	box-sizing: border-box;
}

.logo svg {
    background-size: 46px!important;
    height: 72px!important;
    padding-left: 1px!important;
    width: 1px!important;
}

header h4:before
{
	background: url(https://i.ibb.co/RyhssTP/mdm-WEB.png) no-repeat!important;
	box-sizing: border-box;
}

header h4:before {
    background-size: 69px!important;
    height: 70px!important;
    padding-left: 70px!important;
    width: 1px!important;
}

}

@-moz-document domain("developer.mozilla.org") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/


 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
__________________________________________________________________________________________________________________________________*/


 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/



 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
