/* ==UserStyle==
@name           Anime/Manga Search Hover Fix
@namespace      ShishioStyles
@version        1
@description    what the title says
@author         Shishio-kun @MyAnimeList
==/UserStyle== */

@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist|editlist).*")   {

.page-common .hoverinfo{
  animation-name: example;
  animation-duration: .5s;
    opacity: 0;
}

@keyframes example {
  0% {opacity: .1;}
  100% {opacity: 1;}
}

.page-common .hoverinfo:hover{
    display: block !important;
        left: 00px;    
    opacity: 1 !important;
}


.page-common .hoverinfo:active{
        opacity: 0;

}

.page-common .hoverinfo:focus{
}

.page-common .hoverinfo.left.top:after {
    background-position: top right;
    background-image: url(https://imgur.com/h7cTj1S.png);
    left: -85px;
    height: 70px;
    width:90px;
    top: 0px;
}

.page-common .hoverinfo.left.top:before {
content: "";
    padding-left: 500px;
    height: 50px;
        padding-top: 50px;
    margin-left: -500px;
        top: 20px;
    position: relative;
}


.page-common .hoverinfo.left.bottom:after {
    background-position:top right;
    background-image: url(https://imgur.com/6SK2Ohq.png);
    left: -85px;
    height: 70px;
    width: 90px;
    bottom: 20px;
}

.page-common .hoverinfo.left.bottom:before {
content: "";
    padding-left: 500px;
    padding-top: 50px;
    margin-left: -500px;
    top: 270px;
    position: relative;
}


.page-common .hoverinfo:after {

    background-position: right top;
    background-repeat: no-repeat;
    content: '';
    display: block;
    height: 43px;
    left: -14px;
    z-index: 3 !important;
    position: absolute;
    top: 30px;
    width: 20px;
}


.page-common .hoverinfo .hoverinfo-contaniner {
     margin-top: -13px !important;}

.hoverinfo_trigger.fw-b.fl-l{

}
}
