css:
@-moz-document url('http://ratchet.wikia.com/wiki/Ratchet_%26_Clank_wiki')
{
/*Making the main page match the theme*/
.rounded_corners,.yellowlinks
{
background: -moz-linear-gradient(-90deg, #265181, #183464) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#265181), to(#fff)) !important;
border: none !important;
}
#maintop_links + hr + table + table
{
background: none !important;
}
.mw-headline span a,.mw-headline a,a[title^="R&C:FU"],.rounded_corners a
{
color: #D3D3D3 !important;
}
table,tr,td
{
border: none !important;
}
#mainnav_links i a,#maintop_links i a,#maintop_links b a
{
color: #D3D3D3 !important;
}
div[style="position: relative; width: 300px; float: right; clear: right;"] table[style="width: 300px;"]
{
border: 2px solid #265181 !important;
-moz-border-radius: 7px !important;
}
}
@-moz-document domain('ratchet.wikia.com')
{
/*Page header*/
#wikia_header
{
background: url('http://img94.imageshack.us/img94/456/wikiheadertop.png') no-repeat !important;
background-position: 50% 0px !important;
}
#background_strip
{
background: url('http://img717.imageshack.us/img717/9782/wikiheaderbottom.png') no-repeat !important;
background-position: 50% 0px !important;
}
#wiki_logo
{
background-image: url('http://img519.imageshack.us/img519/6827/wikih.png') !important;
}
.header-button
{
background-image: url('data:image/png;base64,') !important;
background-color: transparent !important;
color: #FFF !important;
-moz-box-shadow: none !important;
line-height: 100% !important;
}
/*links at the top right*/
#userData
{
margin-top: -5px !important;
}
#userData span a
{
font-size: 11pt !important;
}
/*sidebar stuff*/
.sub-menu
{
-webkit-transition: display 0.5s linear !important;
-moz-box-shadow: 0px 0px 6px #404040 !important;
-webkit-box-shadow: 0px 0px 6px #404040 !important;
-moz-border-radius: 3px !important;
-webkit-border-radius: 3px !important;
left: 202px !important;
top: -3px !important;
}
.activity-type-edit.activity-ns-0 img
{
margin-left: -24px !important;
}
#widget_101-recently-edited
{
margin-left: 29px !important;
}

.sprite.history
{
position: static !important;
background: url('data:image/png;base64,') no-repeat !important;
height: 0px !important;
width: 0px !important;
padding-left: 20px !important;
padding-top: 20px !important;
margin-top:0px !important;
left: 0px !important;
}
/*bookmarks widget stuff*/
.WidgetBookmarkRemove:hover
{
float: right !important;
font-family: Arial Black !important;
font-weight: normal !important;
text-decoration: none !important;
border: 1px solid #B7BBBE !important;
-moz-border-radius: 2px !important;
width: 12px !important;
height: 12px !important;
line-height: 11px !important;
text-align: center !important;
}
.WidgetBookmarkRemove:active
{
float: right !important;
font-family: Arial Black !important;
font-weight: normal !important;
text-decoration: none !important;
border: 1px solid #5C2116 !important;
background: -moz-linear-gradient(-90deg, #802E1F, #BF4F33) !important;
-moz-border-radius: 2px !important;
width: 12px !important;
height: 12px !important;
line-height: 11px !important;
text-align: center !important;
color: white !important;
}
.WidgetBookmarkRemove
{
float: right !important;
font-family: Arial Black !important;
font-weight: normal !important;
border: 1px solid transparent !important;
width: 12px !important;
height: 12px !important;
line-height: 11px !important;
text-align: center !important;
}
/*correcting the positioning of buttons on the bookmarks widget*/
#widget_1_close
{
left: -3px !important;
top: -1px !important;
}
.addBookmark
{
top: -27px !important;
left: 155px !important;
}
/*shadow for widgets that are being dragged*/
.ui-sortable-helper
{
-moz-box-shadow: 0px 0px 6px black !important;
-webkit-box-shadow: 0px 0px 6px black !important;
}
/*background*/
body
{
background-attachment: fixed !important;
background-image: url('http://img521.imageshack.us/img521/2863/background2y.jpg') !important;
background-position: 50% 150px !important;
background-repeat: no-repeat !important;
}
/*category bar at the bottom of articles*/
#catlinks
{
background-image: url('data:image/png;base64,') !important;
}
div[class^="toolbar neutral"]
{
display: none !important;
}
/*search bar stuff*/
#search_field
{
height:20px !important;
width:190px !important;
border: none !important;
top: 3px !important;
left: 3px !important;
}
#search_box
{
height:22px !important;
}
#searchform
{
height: 20px !important;
}
#search-button
{
background: url('data:image/png;base64,') no-repeat !important;
bottom: 3px !important;
border: none !important;
}
/*changing the images on the "Create new article" and "Upload new image" links in the sidebar*/
img#dynamic-links-write-article-img
{
height: 0 !important;
width: 0 !important;
padding-left: 20px !important;
padding-top: 20px !important;
background: url('data:image/png;base64,') no-repeat !important;
clip:rect(0px, 20px, 20px, 0px) !important;
}
img#dynamic-links-add-image-img
{
height: 0 !important;
width: 0 !important;
padding-left: 20px !important;
padding-top: 20px !important;
background: url('data:image/png;base64,') no-repeat !important;
clip:rect(0px, 20px, 20px, 0px) !important;
left: 0px !important;
}
#dynamic-links-add-image-link
{
margin-left: 5px !important;
}
#dynamic-links-write-article-link
{
margin-left: 5px !important;
}
/*link box
#link_box
{
border-top: 1px solid #AAAAAA !important;
background-color: #EEEEEE !important;
}*/
/*getting rid of the "Share" button on the page bar*/
li#control_share_feature
{
display:none !important;
}
/*getting rid of the "create new wiki" button in the page header*/
#requestWikiData
{
display: none !important;
}
/*rounding avatar borders*/
#user_masthead a img.avatar
{
-moz-border-radius: 4px !important;
-webkit-border-radius: 4px !important;
}
/*page tabs stuff, making them pop up on hover*/
#page_tabs 
{
margin-top: 2px !important;
}
#page_tabs li a.new
{
text-shadow: none !important;
color: white !important;
opacity: .5 !important;
}
#page_tabs li:hover a.new
{
text-shadow: none !important;
color: white !important;
}
#page_tabs li a:hover
{
text-decoration: none !important;
}
#page_tabs li.selected a:hover
{
text-decoration: underline !important;
}
#page_tabs li.selected a
{
background: transparent !important;
}
#page_tabs li:hover 
{
background-color: rgba(255,255,255,.5) !important;
border: none !important;
height: 26px !important;
top: 0px !important;
}
#page_tabs li 
{
background-color: rgba(255,255,255,.3) !important;
border: none !important;
height: 24px !important;
top: 2px !important;
}
#page_tabs li.selected 
{
-moz-box-shadow: 0px 0px 5px black !important;
background: -moz-linear-gradient(top, #D8D8D8, #FFF 50%) !important;
border: none !important;
height: 26px !important;
top: 0px !important;
}
#page_tabs li a.selected.new
{
-moz-box-shadow: 0px 0px 5px black !important;
background: -moz-linear-gradient(top, #D8D8D8, #FFF 50%) !important;
border: none !important;
height: 26px !important;
top: 0px !important;
color: red !important;
opacity: 1 !important;
-moz-box-shadow: none !important;
}
/*suff with the user masthead*/
div#user_masthead
{
background: -moz-linear-gradient(-90deg, #265181, #183464) !important;
background: -webkit-gradient(linear, left top, left bottom, from(#265181), to(#fff)) !important;
}
body.masthead-regular #page_bar, body.masthead-special #page_bar
{
background-color: #265181 !important;
background-image: none !important;
border-top: 1px solid #FFF !important;
border-bottom: 1px solid #AAAAAA !important;
-moz-border-radius: 0px !important;
-webkit-border-radius: 0px !important;
height: 28px !important;
}
body.masthead-regular #page_controls li a
{
line-height: 28px !important;
margin-top: 0px !important
}
body.masthead-regular #page_controls li img
{
top: 4px !important;
}
body.masthead-special #page_bar
{
height: 5px !important;
}
/*user masthead tabs stuff, making them pop up on hover*/
#user_masthead_tabs
{
top: 38px !important;
}
ul#user_masthead_tabs li.color1.selected
{
background-image: url('data:image/png;base64,') !important;
border-color: #DFE5EC !important;
height: 25px !important;
top: 1px !important;
}
ul#user_masthead_tabs li
{
padding: 0px !important;
display: block !important;
height: 23px !important;
top: 3px !important;
}
ul#user_masthead_tabs li:hover
{
padding: 0px !important;
display: block !important;
height: 25px !important;
top: 1px !important;
}
ul#user_masthead_tabs li.selected a:hover
{
text-decoration: underline !important;
}
ul#user_masthead_tabs li.selected a
{
background-color: transparent !important;
}
ul#user_masthead_tabs li
{
-webkit-transition: background-color 0.1s linear !important;
-webkit-transition: top 0.1s linear !important;
background-color: rgba(78,109,142,.6) !important;
}
ul#user_masthead_tabs li.color1:hover
{
background-color: #215589 !important;
}
#user_masthead_tabs li:hover a
{
text-decoration: none !important;
}
ul#user_masthead_tabs li a
{
background-color: transparent !important;
display: block !important;
padding: 7px 20px 3px !important;
height: 15px !important;
}
/*no more green on the menu that pops up when you hover over avatars*/
div#wk-avatar-username + ul li a
{
background-color: transparent !important;
}
#wk-avatar-arrow
{
display: none !important;
}
/*new messages notifiaction*/
div.usermessage
{
border-color: #5B89B3 !important;
background-color: #265181 !important;
-moz-border-radius: 4px !important;
-webkit-border-radius: 4px !important;
border-width: 2px !important;
color: #FFF !important;
background-image: url('data:image/png;base64,') !important;
background-repeat: no-repeat !important;
background-position:2px 1px !important;
}
div.usermessage a
{
color: #DFE5EC !important;
}
/*Latest activity widget*/
.community_header
{
background: white !important;
border-color: #183767 !important;
color: black !important;
}
/*changing the image on widget headers*/
.headerMenuButton dd,.headerMenuButton dt
{
background-image: url('data:image/png;base64,') !important;
}
/*changing the wikia logo to match the theme*/
#wikia_logo
{
background-image:url('data:image/png;base64,') !important;
}
/*centering arrows on the sidebar*/
div.menu-item a em
{
top: -2px !important;
}
/*hover effects on menu items*/
.navigation-hover, .navigation-hover a:hover
{
background-image: url('data:image/png;base64,') !important;
-moz-border-radius: 3px !important;
-webkit-border-radius: 3px !important;
color: white !important;
}
/*search bar autocomplete box*/
div[id^="Autocomplete"]
{
top: 27px !important;
border: 1px solid black !important;
-moz-box-shadow: 0px 0px 6px #404040 !important;
-webkit-box-shadow: 0px 0px 6px #404040 !important;
-moz-border-radius: 5px !important;
-webkit-border-radius: 5px !important;
}
/*rounding of stuff*/
#wikia_page
{
-moz-border-radius: 0.4em !important;
-webkit-border-radius: 0.5em !important;
}
#page_tabs li, #sidebar_1 dt 
{
-moz-border-radius-topleft:0.4em !important;
-moz-border-radius-topright:0.4em !important;
-webkit-border-top-left-radius:0.4em !important;
-webkit-border-top-right-radius:0.4em !important;
}
.rounded_corners, #wikia_page, #catlinks, #search_field, #search_box, #link_box, #navigation_widget, #sidebar_1 dl, .community_details, #page_bar, .wk_blogs_title 
{
-moz-border-radius:0.4em 0.4em 0.4em 0.4em !important;
-webkit-border-radius:0.4em 0.4em 0.4em 0.4em !important;
}
/*changing the page bar to fit the theme*/
#page_bar
{
background-image:url('data:image/png;base64,') !important;
-moz-border-radius: 0.4em !important;
-webkit-border-radius: 0.3em !important;
border-bottom-width: 0px !important;
}
/*search bar box background*/
#search_box
{
background-image:url('data:image/png;base64,') !important;
}
/*widget title bar background*/
.widget_title
{
background-image:url('data:image/png;base64,') !important;
}
/*hover effects on the page bar buttons, also changing their images*/
#page_controls
{
position: absolute !important;
}
#page_controls li
{
-webkit-transition: background-color 0.1s linear !important;
padding-left: 4px !important;
padding-right: 4px !important;
margin-right: 0px !important;
position: relative !important;
-moz-border-radius: 6px !important;
-webkit-border-radius: 6px !important;
}
#page_controls li:hover
{
background-color: rgba(24, 57, 105, 0.3) !important;
padding-left: 4px !important;
padding-right: 4px !important;
margin-right: 0px !important;
-moz-border-radius: 6px !important;
-webkit-border-radius: 6px !important;
}
#control_edit img
{
position: static !important;
background:url('data:image/png;base64,') no-repeat !important;
height: 0px !important;
width: 0px !important;
padding-left: 20px !important;
padding-top: 20px !important;
margin-top:0px !important;
left: 0px !important;
}
#page_controls li img
{
position: absolute !important;
top: 6px !important;
height: 0px !important;
width: 0px !important;
padding-left: 20px !important;
padding-top: 20px !important;
left: 3px !important;
background-repeat: no-repeat !important;
background-position: 0px 0px !important;
}
#page_controls li a:hover
{
text-decoration: none !important;
}
#page_controls li a
{
left: 0px !important;
margin-left: -4px !important;
margin-right: -2px !important;
padding-left: 26px !important;
padding-right: 2px !important;
display: block !important;
font-size: 11pt !important;
}
#control_viewsource img
{
background:url('data:image/png;base64,') no-repeat !important;
}
#ca-viewsource
{
display:block !important;
}
#control_history img
{
background-image:url('data:image/png;base64,') !important;
}
#control_history a
{
left: 5px !important;
}
#control_move img
{
background-image:url('data:image/png;base64,') !important;
}
#control_move a
{
left: 5px !important;
}
#control_unwatch img
{
background-image:url('data:image/png;base64,') !important;
}
#control_unwatch a
{
left: 5px !important;
}
#control_watch img
{
background:url('data:image/png;base64,') no-repeat !important;
}
#control_watch a
{
left: 5px !important;
}
#control_addsection img
{
background-image:url('data:image/png;base64,') !important;
margin-top: 5px !important;
margin-left: 4px !important;
}
#control_addsection a
{
left: 5px !important;
}
/*background for "save page/preview" bar that appears when editing*/
.edit_enhancements_toolbar_fixed
{
-moz-border-radius: 7px !important;
}
#edit_enhancements_toolbar
{
background: -moz-linear-gradient(-90deg, #265181, #183464) !important;
border: none !important;
-moz-border-radius-bottomleft: 7px !important;
-moz-border-radius-bottomright: 7px !important;
margin-top: -1px !important;
}
label[for^="wpSummary"] span a
{
color: white !important;
}
/*button styles*/
a.wikia-button, span.wikia-button a, input[type="submit"], input[type="reset"], input[type="button"], button, .wikia-gallery-add.noprint
{
background: -moz-linear-gradient(-90deg, #265181, #183464) !important;
-moz-box-shadow: 0px 0px 0px 1px #265181 !important;
line-height: 17px !important;
}
.wikia-button.big a span
{
line-height: 30px !important;
}
a.wikia-button:active, span.wikia-button a:active, input[type="submit"]:active, input[type="reset"]:active, input[type="button"]:active, button:active, .wikia-gallery-add.noprint:active
{
background: -moz-linear-gradient(-90deg, #183464, #265181) !important;
-moz-box-shadow: 0px 0px 0px 1px #265181 !important;
line-height: 17px !important;
}
a.wikia-button:hover, span.wikia-button a:hover, input[type="submit"]:hover, input[type="reset"]:hover, input[type="button"]:hover, button:hover, .wikia-gallery-add.noprint:hover
{
background: -moz-linear-gradient(-90deg, #2D6199, #1D427C) !important;
-moz-box-shadow: 0px 0px 0px 1px #265181 !important;
line-height: 17px !important;
}
a.wikia-button.secondary, span.wikia-button.secondary a, input.secondary[type="submit"], input.secondary[type="reset"], input.secondary[type="button"], button.secondary {
-moz-box-shadow:0 0 0 1px #999999 !important;
background-color:#CCCCCC !important;
background-image:-moz-linear-gradient(center top , #CCCCCC 20%, #AAAAAA 70%) !important;
border-color:#E2E2E2 !important;
color:#333333 !important;
}
/*Gallery*/
.wikia-gallery.accent
{
background-color: rgba(24, 57, 105, 0.3) !important;
background-image: none !important;
-moz-border-radius: 6px !important;
-webkit-border-radius: 6px !important;
}
.wikia-gallery-item div div
{
background: none !important; 
border: none !important;
width: auto !important;
height: auto !important;
}
.wikia-gallery-item div div a img
{
background-color:#FFFFFF !important;;
border: 1px solid #AAAAAA !important;;
padding:3px !important;
}
.wikia-gallery-add.noprint a
{
color: white !important;
}
.wikia-gallery-add.noprint:hover a
{
color: white !important;
text-decoration: none !important
}
.wikia-gallery-add.noprint img
{
height: 0 !important;
width: 0 !important;
padding-left: 20px !important;
padding-top: 20px !important;
background: url('data:image/png;base64,') no-repeat !important;
clip:rect(0px, 20px, 20px, 0px) !important;
left: 0px !important;
top: 2px !important;
}
}
