css:
@-moz-document domain("roblox.com")
{

  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }

  .ad,.ad *,.img_ad,.ad-annotations,#google_image_div,#aw0,#AdvertisingLeaderboard
  {
    display: none !important;
  }
/*Style by @Keixsra*/
  #header
  {
    background-color: #ffe8ee;
  }

  .navigation .notification-icon, .rbx-highlight
  {
    background: #ffe8ee;
  }

  #chat-header
  {
    background: #ffe8ee;
  }

  .slide-item-container-left,.profile-avatar-right
  {
    background: #ffe8ee;
  }

  .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading, .rbx-tabs-horizontal .rbx-tab-heading:hover, .rbx-tabs-horizontal .rbx-tab-heading:focus, .rbx-tabs-horizontal .rbx-tab-heading:active
  {
    box-shadow: 0 -4px 0 .6 #ffe8ee inset;
  }

  .forum-table-header, body .forum-table-header th, body .forum-table-footer, #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th, #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th
  {
    background: #ffe8ee !important;
  }

  .rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more, #AjaxCommentsContainer > div > div.comment-form > form > button, .rbx-vip-server-create, .btn-secondary-xs
  {
    background-color: #ffe8ee !important;
    border-color: #ffe8ee;
  }

  .rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover, .rbx-vip-server-create:hover, .btn-secondary-xs:hover
  {
    background: #ffe8ee !important;
    border-color: #ffe8ee;
  }

  #TopAbpContainer, .ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer, #Skyscraper-Adp-Right, #Skyscraper-Adp-Left,
.abp-spacer, #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even, #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd, #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even, #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd, .sponsored-game, .alert-info, #mCSB_1_container > ul > li.font-bold.small, #mCSB_1_container > ul > li.rbx-nav-sponsor
  {
    display: none !important;
  }

  #navbar-search-input, #navbar-search-btn
  {
    border: 1px solid #ffe8ee;
  }

  body
  {
    background: transparent !important;
  }

  .content
  {
    background: transparent !important;
  }

  .container-footer
  {
    position: static;
  }

  html
  {
    width: 100% !important;
    height: 100% !important;
    /*background-image: url((https://i.pinimg.com/originals/db/fb/0b/dbfb0b13ad38331b8590a87e39fca51a.jpg) !important;*/
    background-size: cover !important;
    background-attachment: fixed !important;
    background-image: url(https://wallpaper.dog/large/20470764.jpg) !important;
  }

  #party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off
  {
    background: linear-gradient(to bottom, #ffe8ee, #ffe8ee);
  }

  #ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4)
  {
    display: none;
  }

  #GamesPageLeftColumn
  {
    width: auto;
    margin-right: 10px !important;
    margin-left: 10px !important;
  }

  #GamesListsContainer > div
  {
    background-color: transparent !important;
    width: 100% !important;
  }

  #navigation
  {
    background-color: #fed4e0;
  }

  .rbx-btn-primary-lg
  {
    background-color: #ffe8ee;
  }

  .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus
  {
    background-color: #ffe8ee;
  }
/*Style by @Keixsra*/
  .avatar-back
  {
    background-image: url(https://wallpaper.dog/large/20470764.jpg);
  }
}
