css:
@-moz-document 
domain("lifehacker.com"), domain("gizmodo.com"),  domain("i.gizmodo.com"), domain("valleywag.com"), domain("gawker.com"), domain("jezebel.com"), domain("defamer.com"), domain("jalopnik.com"), domain("io9.com"), domain("deadspin.com"), domain("kotaku.com")
{


/*  REMOVE THE SINGLE LINE BELOW IF YOU WANT LINK COLORS TO BE SITE SPECIFIC  */


a {color: #999 !important}





BODY {background: #111 !important; color: #999 !important}

#header { background: #222 !important; border: solid 20px #222 !important; -moz-border-radius: 20px 20px 20px 20px !important}

#splashPosts  {width: 992px !important; background: #444 !important; border: solid 4px #444 !important; -moz-border-radius: 6px!important}

#menubar {width: 1040px !important; background: #222 !important; border: solid 4px #111 !important; margin-left: -24px !important; -moz-border-radius: 20px 20px 20px 20px !important; margin-top: 10px !important}

#container { width: 1040px !important; background: #444 !important; -moz-border-radius: 20px 20px 20px 20px !important; padding-top: 6px !important}

#masthead { background: #444 !important; -moz-border-radius: 6px !important}

#siteInfo, #skySpacer, #spacer160 {display: none !important}

a:hover {color: #ccc !important; text-decoration: none !important}

.content {color: #ccc !important;}

.topTag {background: #222 !important; border: solid 1px #999 !important;}

.post {-moz-border-radius: 8px !important; background: #222 !important; border: solid 8px #222 !important; }

.bigExcerptHolder {background: #444 !important; margin: 0px !important; padding: 4px !important}

.spimage {margin: 0px !important;}

.content.postBigPic {background: #444 !important;}

.comment:hover {background: #444 !important}

.commentbox_widget.commentbox {background: #222 !important; width: 338px !important}
 
.cb_wrapper.smalltype {background: #444 !important; border: solid 2px #444 !important; -moz-border-radius: 4px !important}

.cb_l.cb_onlyexp.cb_default {border: none !important}

.related_posts {background: #444 !important}

.rp_item {background: #222 !important; -moz-border-radius: 8px !important}

.postmeta_permalink {border-bottom: solid 2px #999 !important}


}
